INSERT INTO `tourist_attractions` (`id`, `name`, `description`, `region_id`, `city_id`, `attraction_Priority`, `location`, `image_url`, `html_file`, `category`) VALUES
(355, 'المسجد الحرام', 'المسجد الحرام هو أقدس وأكبر مسجد في الإسلام، ويقع في مكة المكرمة بالمملكة العربية السعودية. يُعتبر أول مسجد في تاريخ الإسلام وأحد أقدس الأماكن في العالم . يحيط بالمسجد الحرام الكعبة المشرفة، التي تعد قبلة المسلمين.', 1, 1, 1, 'https://maps.app.goo.gl/28LQqoNe9NwWXQdc8', 'https://www.rcmc.gov.sa/getmedia/11dbb750-f60c-4898-888f-d66f0589f1da/_%d9%85%d8%b9%d8%a7%d9%84%d9%85-%d9%85%d9%83%d8%a9.jpg', 'The_Grand_Mosque.html', 'دينية'),
(356, 'برج الساعة', 'برج الساعة هو أحد أبرز المعالم في مكة المكرمة، ويقع بالقرب من المسجد الحرام. يُعتبر من أطول الأبراج في العالم، حيث يبلغ ارتفاعه نحو 601 مترًا. تم الانتهاء من بناء البرج في عام 2012 ويُعتبر جزءًا من مشروع الملك عبد الله في منطقة مكة المكرمة.', 1, 1, NULL, 'https://maps.app.goo.gl/7tWxzPQWHmUnEo6X6', 'https://media.assettype.com/sabq%2Fimport%2Fuploads%2Fmaterial-file%2F5cf56c2aa1fc8f272e8b4568%2F5cf56c22238a7.jpg?w=1024&auto=format%2Ccompress&fit=max', 'Clock_Tower.html', 'دينية'),
(357, 'منى', 'منى هي منطقة تقع في مكة المكرمة وتعد أحد المواقع الهامة في مناسك الحج. يمر الحجاج في منى خلال أيام التشريق، وهي الأيام التي تلي يوم عيد الأضحى، حيث يقيمون فيها ويؤدون بعض شعائر الحج.', 1, 1, NULL, 'https://maps.app.goo.gl/xa4fmRVYoAUXPdgS8', 'https://upload.wikimedia.org/wikipedia/commons/3/31/Some_perspective_on_Mina_-_Flickr_-_Al_Jazeera_English.jpg', 'Mona.html', 'دينية'),
(358, 'مزدلفة', 'مزدلفة هي أحد المشاعر المقدسة الثلاثة في الحج، وتقع بين مشعري عرفات ومنى، حيث يبيت فيها الحجاج بعد نفرتهم من عرفات، ويجمعون الحصى لرمي الجمرات ويُصَلّون فيها صلاتي المغرب والعشاء جمعًا وقصرًا قبل أن يفيضوا إلى منى في يوم عيد الأضحى. وتسمى أيضًا بالمشعر الحرام، وهي واجبة المبيت على الحجاج', 1, 1, NULL, 'https://maps.app.goo.gl/QZGXfJWyoRv7Ecur5', 'https://media.assettype.com/sabq%2Fimport%2Fuploads%2Fmaterial-file%2F60f44f53968e9501698b456d%2F60f44f4f5572f.jpeg?w=1024&auto=format%2Ccompress&fit=max', 'Muzdalifah.html', 'دينية'),
(359, 'عرفة', 'عَرَفَةُ هوَ جبل في المملكة العربية السُعودية يقع على الطَريق بين مكة وَالطائف، حيث يَبعُد عن مكة حوالي 22 كيلومترا وعلى بُعد 10 كيلومترات من منى و 6 كيلومترات من مزدلفة. تُقام عنده أَهم مناسك الحج، والتي تسمى بوقفة عرفة وذلك في يوم التاسع من شهر ذي الحجة. وتعد الوقفة بعرفة أهم مناسك الحج كما قال الرسول محمد: «الحَجُّ عَرَفَةُ». يصل طول جبل عرفة إلى 300 متر، ووسط قمته شاخص طوله سبعة أمتار ليميزه عن بقية الجبال.', 1, 1, NULL, 'https://maps.app.goo.gl/z4xi2X4jGc8PPr9PA', 'https://alraynews.net/wp-content/uploads/2025/06/729d8203-301e-4fa4-94fb-972f352990aa.jpeg?v=1749121032', 'Arafah.html', 'دينية'),
(360, 'جبل النور', 'يعد جبل النور، الذي يرتفع حوالي 640 مترًا، واحدًا من أكثر المواقع الروحية للزيارة في مكة. غار حراء، الموجود في قمة الجبل، هو المكان الذى تلقى فيه النبي محمد ﷺ آيات القران الكريم، مما يجعله موقعًا روحانياً يجتذب الزوار للصعود والتأمل.', 1, 1, NULL, 'https://maps.app.goo.gl/G7Q6gqGS4WRvFm8FA', 'https://www.rcmc.gov.sa/getmedia/403de815-8d82-4adb-8064-0948af452ea8/Alnoor1.jpg?width=750&height=463&ext=.jpg', 'Mount_of_Light.html', 'دينية'),
(361, 'جبل ثور', 'يقع جبل ثور جنوب مكة المكرمة ومن تسمية جبل ثور اشتق اسم الغار، غار ثور الذي مكث فيه الرسول محمد بن عبد الله ﷺ وأبو بكر الصديق رضي الله عنه ثلاث ليالٍ أثناء هجرته إلى المدينة المنورة. وفي أثناء وجودهما في الغار جاءت قريش تبحث عنهما، حتى وقفت على فم الغار، إلا أن الله ردها بفضله وقدرته، يقول أبو بكر رضي الله عنه: لو أن أحدهم نظر إلى قدميه لأبصرنا. فقال له النبي ﷺ : ( يا أبا بكر ما ظنك باثنين الله ثالثهما ) رواه البخاري ومسلم . وقد ذكر الله تعالى هذا الحادثة في كتابه فقال سبحانه: إِلَّا تَنْصُرُوهُ فَقَدْ نَصَرَهُ اللَّهُ إِذْ أَخْرَجَهُ الَّذِينَ كَفَرُوا ثَانِيَ اثْنَيْنِ إِذْ هُمَا فِي الْغَارِ إِذْ يَقُولُ لِصَاحِبِهِ لَا تَحْزَنْ إِنَّ اللَّهَ مَعَنَا فَأَنْزَلَ اللَّهُ سَكِينَتَهُ عَلَيْهِ وَأَيَّدَهُ بِجُنُودٍ لَمْ تَرَوْهَا وَجَعَلَ كَلِمَةَ الَّذِينَ كَفَرُوا السُّفْلَى وَكَلِمَةُ اللَّهِ هِيَ الْعُلْيَا وَاللَّهُ عَزِيزٌ حَكِيمٌ.', 1, 1, NULL, 'https://maps.app.goo.gl/cmJphYJAQ7cbPuah8', 'https://www.rcmc.gov.sa/getmedia/6efbdbce-a0e4-48c9-b9a4-ea3c6710c3f6/%d8%ab%d9%88%d8%b1.jpg?width=750&height=511&ext=.jpg', 'Mount_Thor.html', 'دينية'),
(362, 'مجرى عين زبيدة', 'يمتد مجرى عين زبيدة من جبل كرا إلى مكة المكرمة، وكانت عينًا غزيرة الماء تم تنفيذها بأمر من زبيدة بنت جعفر المنصور، زوجة الخليفة العباسي هارون الرشيد، في حوالي عام 195هـ. بدأت القناة من \"عين وادي نعمان\" أسفل جبل كرا في الطائف، وتوجهت إلى \"الأوجر\" في وادي نعمان، حيث تم جرّ المياه في قنوات أرضية إلى عرفة. وقد تم توجيه المياه إلى جبل الرحمة عبر محيطه، مع فروع للقنوات التي تزوّد البرك في عرفة بالماء ليستفيد منها الحجاج. كما تم تجهيز أماكن خاصة تحتوي على حنفيات حجرية يسهل استخدامها.', 1, 1, NULL, 'https://maps.app.goo.gl/HFMYxBXCfojCdFqL6', 'https://www.rcmc.gov.sa/getmedia/1a4037a6-b92a-419a-af69-0a563e3129f9/0I6A7475.jpg?width=750&height=498&ext=.jpg', 'Zubayda_spring.html', 'تراثية'),
(363, 'مسجد أم المؤمنين السيدة عائشة', 'مسجد السيدة عائشة رضي الله عنها أحد مساجد مكة المكرمة، يقع على مسافة 7 كم عن الحرم المكي الشريف خارج حدود المسجد الحرام. اكتسب شهرته من كونه بُني في الموضع الذي أحرمت منه أم المؤمنين عائشة بنت أبي بكر بالعمرة في حجة الوداع سنة 9 للهجرة.', 1, 1, NULL, 'https://maps.app.goo.gl/XDK5fccyoibGV6pp6', 'https://www.rcmc.gov.sa/getmedia/d7e3df52-1555-44a1-a0d8-32e54424277d/Eltanemmousk0.png?width=750&height=462&ext=.png', 'Umm_al_Mu\'minin_Sayyida_Aisha_Mosque.html', 'دينية'),
(364, 'الحديبية', 'الحديبية تقع على بعد 22 كم غربي المسجد الحرام، وتعرف اليوم باسم \"الشميسي\". نزلَ فيها رسول الله ﷺ ومعه 1400 من صحابته قاصدين مكة لأداء العمرة في السنة السادسة من الهجرة، فمَنَعَتهم قريش من دخول مكة. كما شهِدَ المكانُ بيعةَ الرضوان، حين أشِيعَ أنَّ مُوفَد الرسول لمفاوضة قريش عثمانَ بن عفان -رضي الله عنه- قد قُتِل، فبايعَه صحابتُه على قتال قريش ونزل في ذلك قوله تعالى: {لَّقَد رَضِيَ اللَّهُ عَنِ المُؤمِنِينَ إِذ يُبَايِعُونَكَ تَحتَ الشَّجَرَةِ فَعَلِمَ مَا فِي قُلُوبِهِم فَأَنزَلَ السَّكِينَةَ عَلَيهِم وَأَثَابَهُم فَتحًا قَرِيبًا}. وجرت على أرض الحديبية مفاوضاتُ بين النبي ﷺ وقريش انتهت بصلح الحديبية؛ فحَلَقَ النبيُّ ﷺ والصحابة رؤوسَهم وذبحوا هديَهم ورجعوا إلى المدينة ذلك العام.', 1, 1, NULL, 'https://maps.app.goo.gl/ZEc2aqF8woTYscNM9', 'https://www.rcmc.gov.sa/getmedia/326315de-14f7-4d21-8f71-ba551491d43e/Elhodaybyah3.png?width=750&height=501&ext=.png', 'Al_Hudaybiyah.html', 'دينية'),
(365, 'مسجد الجنّ', 'تشير المصادر الى أنه سُمي بمسجد الجن لأنه بُني عند المكان الذي اجتمع فيه رسول الله صلى الله عليه وسلم بالجن، يُسمى أيضاً مسجد الحرس، لأن صاحب الحرس كان يطوف بمكة المكرمة حتى إذا انتهى إليه وقف عنده ولم يَجُزه حتى يتوافى عنده عرفاؤه وحرسُه. ومسجد الجن من المساجد التي كانت قائمةً بمكة منذ القرن الثاني الهجري، وشهد عدة تجديدات خلال عصر الخلافة الإسلامية والتي استمرت حتى عصرنا الحاضر.', 1, 1, NULL, 'https://maps.app.goo.gl/b9cnUDzKC7Bb3VmC7', 'https://www.rcmc.gov.sa/getmedia/0a53e081-7ca2-43e3-bb1e-eb2a5f29aa51/Aljin.jpg?width=750&height=581&ext=.jpg', 'Mosque_of_the_Jinn.html', 'دينية'),
(366, 'مسجد الجعرانة', 'المسجد بُني قبل القرن الثالث الهجري / التاسع الميلادي، ومنه يعتمر أهل مكة المكرمة، وقد اعتمر النبي صلى الله عليه وسلم منها بعد غزوة الطائف، ونزل بها الرسول صلى الله عليه وسلم بعد عودته من غزوة حنين وأوطاس في السنة الثامنة من الهجرة (630 م)، ووزع فيها الغنائم، وسئل أنس بن مالك رضي الله عنه: (كم حج النبي صلى الله عليه وسلم؟ قال: حجة واحدة. واعتمر أربع عُمَر: عمرة في ذي القعدة، وعمرة الحديبية، وعمرة مع حجته، وعمرة الجعرانة، إذ قسم غنيمة حنين).', 1, 1, NULL, 'https://maps.app.goo.gl/ykrHViU3MQksA7xr9', 'https://www.rcmc.gov.sa/getmedia/76e808d2-c86c-48d0-8f0b-9075e30967bc/%d9%85%d8%b3%d8%ac%d8%af-%d8%a7%d9%84%d8%ac%d8%b9%d8%b1%d8%a7%d9%86%d8%a9-3_3.jpg?width=1465&height=967&ext=.jpg', 'Al_Ja\'ranah_Mosque.html', 'دينية'),
(367, 'مسجد البيعة', 'يقع \"مسجد البيعة\" في أسفل جبل \"ثبير\" مما يلي منى، وسمي بـ (البيعة) لِمبايعة الأنصار لرسول الله ﷺ فيه بحضور عمه العباس بن عبدالمطلب في السنة الثالثة عشر من البعثة حيث بايعوه على الإسلام، ودعَوه للهجرة الى المدينة لمؤازرته ونصرته، وعرفت تلك البيعة باسم بيعة العقبة لأنها تمت في الشِعب الذي عند عقبة منى، وبُني المسجد في عهد الخليفة العباسي \"أبي جعفر المنصور\" سنة 144هـ/761م، واتضح ذلك في نقشين كتابيين: أحداهما مثبت في جداره القِبلي، والأخر في الجدار الجنوبي، وأجريت للمسجد عمارةٌ أخرى في عهد الخليفة العباسي \"المستنصر بالله أبي جعفر منصور بن محمد الظاهر\" عام 625هـ/1227م، وشهد عدة تجديدات خلال عصر الخلافة الإسلامية.', 1, 1, NULL, 'https://maps.app.goo.gl/8Bfiw2wfdAoNYVEt8', 'https://www.rcmc.gov.sa/getmedia/6f45c5ed-87e8-4472-afb1-8ce56c554978/Elbayaamousk.png?width=750&height=500&ext=.png', 'Al_Bay\'ah_Mosque.html', 'دينية'),
(368, 'مسجد الخيف', 'سمي الخيف نسبة إلى خيف بني كنانة في سفح جبل منى الجنوبي بالقرب من الجمرة الصغرى، والخيف لغة هو ما انحدر عن غلظ الجبل وارتفع عن مسيل الماء ، كان مسجد الخيف ولا يزال موضع اهتمام وعناية قيادة المملكة ،لما له من مكانة تاريخية ترتبط بمشعر منى وقد تمت توسعته وإعادة عمارته سنة 1407 هـ الموافق 1987 وبتكلفة تسعين مليون ريال', 1, 1, NULL, 'https://maps.app.goo.gl/tp86MvbJvyKvXmqs6', 'https://www.rcmc.gov.sa/getmedia/a2d246e3-e1f1-4af0-b701-c52544d91d77/Alkaif.jpg?width=750&height=349&ext=.jpg', 'Al_Khaif_Mosque.html', 'دينية'),
(369, 'مراكز ومتاحف برج الساعة', '(مركز علوم الكون) يتكون مركز علوم الكون (المتحف) من أربعة أدوار: الدور الأول: مخصص لعرض ما يتعلق بساعة مكة، من حيث الإنشاء والتقنيات المستخدمة لتحديد الوقت. كذلك توجد الشرفة التي تطل على الكعبة المشّرفة. الدور الثاني: مخصص لكل ما يتعلق بقياس الوقت وابتكارات الإنسان وتوثيق محاولاته لمعرفة الوقت. الدور الثالث: مخصص للشمس والأرض والقمر وما يتعلق بها. الدور الرابع: مخصص للاطلاع على ما يتعلق بالكون عموماً، ومن ذلك النجوم والأبراج والظواهر الفلكية. مركز الأهلة: يتكون مركز الأهلة من سبعة أدوار تقع في قمة برج الساعة في مكة المكرمة: الدور الأول: الردهة (قاعة الاستقبال ومكاتب إدارية) الدور الثاني: مكتبة الأهلة الدور الثالث: معرض الأهلة (أطوار القمر) الدور الرابع: أقسام البحوث (مركز مواقيت الصلاة، واتجاه القبلة) الدور الخامس: لجنة الأهلة الدور السادس: المصلى الدور السابع: ردهة قمة البرج مرصد مكة:', 1, 1, NULL, 'https://maps.app.goo.gl/JiUzzoENgrbS4ZHL7', 'https://media.assettype.com/sabq%2Fimport%2Fuploads%2Fmaterial-file%2F5cd6a2e893f33552318b45a4%2F5cd6a2ce47180.jpg?w=640&auto=format%2Ccompress', 'Clock_Tower_Centers_and_Museums.html', 'تراثية'),
(370, 'مشروع الملك عبدالله بن عبد العزيز لسقيا زمزم', 'أمر الملك عبد العزيز بن عبد الرحمن آل سعود –رحمه الله– بإنشاء سبيل لسقيا ماء زمزم سنة 1345هـ، وأمر بإنشاء سبيل ثاني لسقيا زمزم سنة 1346هـ، وأمر بإصلاح بئر زمزم وتنظيفها ووضع غطاء عليها سنة 1346هـ. وأمر خادم الحرمين الشريفين الملك عبد الله بن عبد العزيز يرحمه الله بإنشــــــــاء محطة تنقية مياه البئر للمحافظة على خصائص ماء زمزم، مع محطة ومصنع تعبئة وخطوط نقل ونظام تحكم ومراقبة وتخزين آلي ومرافق أخرى سنة 1427هـ، وحرص خادم الحرمين الشريفين الملك سلمان بن عبد العزيز آل سعود منذ توليه الحكم على استمرار هذا المشروع.', 1, 1, NULL, 'https://maps.app.goo.gl/6KXz7GTdAvDiqh7M6', 'https://www.rcmc.gov.sa/getmedia/2a10c50b-9184-43c9-b5b7-4dec9733a29b/Heraa4.png?width=750&height=500&ext=.png', 'King_Abdullah_bin_Abdulaziz_Zamzam_Water_Project.html', 'تراثية'),
(371, 'معهد المخطوطات وإحياء التاريخ الإسلامي', 'أنشأت جامعة أم القرى مركز إحياء التراث الإسلامي عــــــــام 1396هـ واستطـــاع المركــز في فتــرات مبكـرة أن يجمــع أمهـــات المخطوطــــــات الإسلامية مــــــن شتى بقــاع العالـــم، واجتمــع لديــــــه في زمـن مبكـــر أكثــر من عشريـــن ألــــــف مخطوطـــــة خدمــــت أعدادًا لا تحصى من أسـاتذة الجامعـات السعوديـــة والعربية والعالمية وطلاب الدراسـات العليــا في الـداخـــل والخــــارج، وتشمل اختصاصــــاته علـــوم الدراســـــــات الإسلامية واللغة العربيـة والدراسـات التاريخيـــــة والحضاريــــة، بالإضافة إلى وظيفتــــــه الأساسيـة في إحيــــاء التـــراث الإسلامي.', 1, 1, NULL, 'https://maps.app.goo.gl/jegKPMMrnFKxLLjk6', 'https://www.rcmc.gov.sa/getmedia/03dae93f-a951-4dbe-9e2b-351c20ddf7ef/Heraa2.png?width=750&height=500&ext=.png', 'Institute_of_Manuscripts_and_Revival_of_Islamic_History.html', 'تراثية'),
(372, 'مركز تاريخ مكة المكرمة', 'استشعارًا من حكومة خــادم الحرمين الشريفين بضرورة وجود مركــز علمي يعمـــل على توثيق ودراســـة و حفظ الإرث التاريخي الحضـــاري لمكة المكرمـــة وإعداد مـا يستلـزم مـن أبحـاث ودراسـات ولقــاءات علميـة لخدمة مكة المكرمــــة والمشـاعر المقدسـة والأماكن التاريخيـة الإسلامية (الماضي ، الحاضر ، المستقبل)فقد صدر أمر خـادم الحرمين الشريفيـن الملك سلمان بن عبد العزيز يحفظه الله ، بإنشـــــاء مركـز يعنى بتاريـخ مكة المكرمة والمدينة المنــــورة وأوكل هذا الأمر لدارة الملك عبدالعزيـــز بإنشـــاء مركز تاريــخ مكة المكرمــــة ليكـون مقـــــره مكة المكرمــــة ويعرض في هذا المعرض صور وكتب تحكي تاريـخ مكة المكرمة والمشاعر المقدسة.', 1, 1, NULL, 'https://maps.app.goo.gl/QRBvbK95uqFXTQXZ9', 'https://www.rcmc.gov.sa/getmedia/4d8e1def-3b9f-4ceb-a852-1583ac328e19/Heraa3.png?width=750&height=500&ext=.png', 'Makkah_History_Center.html', 'تراثية'),
(373, 'مجمع الملك عبد العزيز للكسوة المشرفة', 'في منتصف عام 1346هـ أصدر الملك عبد العزيز بن عبد الرحمن آل سعود يرحمه الله أمرًا بإنشاء دار خاصة لصناعة كسوة الكعبة. واستمر العمل في إنشاء مصنع كسوة الكعبة المشرفة إلى أن تم تجديد المصنع وتحديثه وافتتح في عـــام1397هـ. أقسام المجمع: يحتوي المجمع على عدة أقسام من أهم هذه الأقسام: 1. قسم الحزام 2. قسم خياطة الثوب 3. قسم المصبغة 4. قسم الطباعة 5. قسم النسيج الآلي 6. قسم النسيج اليدوي 7. قسم المختبر ويعمل في تلك الأقسام أكثر من مائتي عامل من العمالة السعودية المؤهلة والمدربة. وبالإضافة إلى إنتاج كسوة الكعبة المشرفة كل عام فإن المجمع ينتج أيضًا الكسوة الداخلية للكعبة المشرفة والكسوة الداخلية للحجرة النبوية الشريفة وعلم المملكة العربية السعودية مطرزًا ومطبوعاً طبقاٌ لنظام علم المملكة العربية السعودية. أوقات الزيارة: يرحب المجمع بزواره من المسلمين والمسلمات لخوض تجربة معرفية فريدة بين حياكة كسوة الكعبة وتطريزها، من الاثنين إلى الخميس من الساعة 08:00 صباحًا حتى 11:00 صباحًا.', 1, 1, NULL, 'https://maps.app.goo.gl/6xyWAXmgcaVosLQ48', 'https://discovermakkah.sa/getContentAsset/7568e832-efab-4fcb-97df-27a384c57e5e/cc721195-df8f-4799-b974-5ade63036cab/King-Abdulaziz-Complex-for-the-Kaaba-Kiswa-(7).jpg?language=ar', 'King_Abdul_Aziz_Complex_for_the_Holy_Kiswa.html', 'تراثية'),
(374, 'متحف عمارة الحرمين الشريفين', 'هو أحد متاحف مكـــة المكرمة، تأسس في عام 1999، يعنى بالحرمين الشريفين والتطور الذي شهدته متحف عمارة الحرمين الشريفين عمارتهما على مدى العصور. القاعات التي يضمها المتحف: قاعة المسجـــــــد الحـــرام قاعة المسجـــــــد النبـــوي قاعة الكعبـــــة المشرفـــة قاعة الصور الفوتوجرافية قاعة المـــدخـــــــل قاعة بئر زمزم قاعة المخطوطات أوقات العمل: من الأحد إلى الخميس من 8:00 صباحا الى 8:00 مساء', 1, 1, NULL, 'https://maps.app.goo.gl/ZXuTndrwGVsY19T48', 'https://www.rcmc.gov.sa/getmedia/1e31ea24-5f13-4e23-b80a-fcdfc6b396c9/clocktower1.png?width=750&height=483&ext=.png', 'Museum_of_the_Architecture_of_the_Two_Holy_Mosques.html', 'تراثية'),
(375, 'أبراج البيت', 'هي واحدة من أبرز المعالم الحديثة في مكة المكرمة، وتقع مباشرة بجوار المسجد الحرام. هذا المجمع الضخم يمثل تحفة معمارية حديثة وهو جزء لا يتجزأ من منظر مكة العمراني. تم تصميمها لتكون مركزًا حيويًا للنشاط الديني والتجاري في مكة المكرمة. المجمع يتكون من سبعة أبراج سكنية وفندقية تحيط بمركز تجاري كبير. التصميم يجمع بين الطراز المعماري الإسلامي التقليدي والعناصر الحديثة، ما يوفر بيئة فاخرة ومريحة للحجاج والزوار. يضم فندق الساعة رويال تاور، أحد أبرز المعالم في أبراج البيت هو فندق الساعة رويال تاور، الذي يشتمل على واحدة من أكبر الساعات في العالم، وهي ساعة مكة. هذه الساعة تُرى من مسافات بعيدة، وهي تتميز بأربع واجهات للساعة تعرض الوقت بطريقة تسهل على الحجاج معرفة أوقات الصلاة. الفندق يوفر أماكن إقامة فاخرة ومباشرة للنظر إلى المسجد الحرام، ما يجعله مثاليًا للزوار الباحثين عن الراحة والروحانية. الأبراج تحتوي على مجموعة واسعة من المرافق التجارية والترفيهية. تشمل هذه التسهيلات مراكز للتسوق تضم مئات المتاجر التي تبيع منتجات محلية ودولية، مطاعم تقدم مأكولات من مختلف أنحاء العالم، ومرافق ترفيهية تلبي احتياجات العائلات والأفراد.', 1, 1, NULL, 'https://maps.app.goo.gl/kGS2GoeWgBUANTJC9', 'https://www.rcmc.gov.sa/getmedia/8cf87145-8b6c-41f6-a848-c284bdeae2b3/Mekkah.png?width=750&height=473&ext=.png', 'Abraj_Al_Bait.html', 'سياحية'),
(376, 'منتزه جعرانة', 'منتزه الجعرانة هو أكبر منتزه في مكة وبه أطول ممشى في مكة كذلك، تبلغ مساحة المنتزه مليون متر مربع تعمل الحديقة 24 ساعة أوقات الحديقة: 24 ساعة. أسعار الدخول: مجانية', 1, 1, NULL, 'https://maps.app.goo.gl/oZ8LYNSNnbnmP3ydA', 'https://www.rcmc.gov.sa/getmedia/bb736181-6d56-4a47-b078-eb817f9933e5/Jiranah.jpg?width=750&height=487&ext=.jpg', 'Jaarana_Park.html', 'ترفيهية'),
(377, 'حديقة الآراك', 'حديقة الأراك حديقة غنّاء، مساحات خضراء، طبيعة جذّابة، هل هذا كل ما تأمله من زيارة حديقة الحسينية مكة المكرمة ؟ بالطبع لا، فهذه الحديقة هي مكان لا يُمكن وصفه بكلمة أو كلمتين، بل يستحق وصفها أسطُر عديدة وينبغي زيارتها على أرض الواقع لتكتشف الحُسن الذي تتميّز به هذه الحديقة والذي خوّلها لتكون أحد أفضل حدائق مكة وأحد أبرز وجهات السياحة في مكة التي لا يُمكن تفويت زيارتها. أوقات الحديقة: 24 ساعة. أسعار الدخول: مجانية', 1, 1, NULL, 'https://maps.app.goo.gl/iG3S1ufB8BZ72ndA7', 'https://www.rcmc.gov.sa/getmedia/2515ff52-9861-421b-b42a-1f8e54351a68/Tadaber1.png?width=750&height=530&ext=.png', 'Arak_Garden.html', 'ترفيهية'),
(378, 'حديقة الملك عبدالله', 'حديقة الملك عبدالله ذات تصميم مميز وتحتوي على أماكن مخصصة للجلوس وألعاب تساعد على نمو عضلات الأطفال ومنها ملعب لكرة القدم وملعب كرة سلة بحجم مناسب. كما يوجد خرائط للممشى ومواقف للسيارات. أوقات الحديقة: تعمل الحديقة 24 ساعة أسعار الدخول: مجانية', 1, 1, NULL, 'https://maps.app.goo.gl/JgjepR1WNKakpLV56', 'https://www.rcmc.gov.sa/getmedia/75959b42-ffa5-4531-bccf-9b290d57ff9b/king-Abdullah-Garden.jpg?width=750&height=500&ext=.jpg', 'King_Abdullah_Park.html', 'ترفيهية'),
(379, 'حديقة وممشى أم المؤمنين زينب الهلالية', 'منتزه للكبار والصغار مساحات عشبيه جيده للجلوس عليها والعاب أطفال وأكشاك بيع الوجبات السريعه فودترك مكان جميل كبير ومنظم يستحق الزياة. تأسست 2010م أوقات الحديقة: تعمل الحديقة 24 ساعة أسعار الدخول: مجانية', 1, 1, NULL, 'https://maps.app.goo.gl/PMtbFqVV4eSyxfoE9', 'https://www.rcmc.gov.sa/getmedia/c58aba6f-c546-45f4-89da-bd91cc86fb08/Um-Almomneen.jpg?width=750&height=456&ext=.jpg', 'Umm_Al_Mu\'minin_Zainab_Al_Hilaliyya_Garden_and_Walkway.html', 'ترفيهية'),
(380, 'المسجد النبوي الشريف', 'يعتبر المسجد النبوي الشريف وزواره بمثابة القاعدة الاقتصادية للمدينة المنورة، حيث يتوافد مئات الآلاف من شتى أنحاء العالم يومياً للصلاة\r\nفي المسجد النبوي الشريف، وقد أولت حكومة خادم الحرمين الشريفين عناية واهتماما بالمسجد النبوي الشريف في تنفيذ التوسعات على مر العصور،\r\nحيث ينتظر أن يستوعب المسجد النبوي بعد انتهاء التوسعة الحالية إلى 1.6 مليون مصلِ بحلول عام 2040م', 3, 2, 1, 'https://maps.app.goo.gl/BncXA6PT5gS9JmSm6', 'https://blog.bayut.sa/uploads/2024/03/Cover_01-4-5.jpg', 'The_Prophet\'s_Mosque.html', 'دينية'),
(381, 'مسجد قباء', 'يمتلك مسجد قباء مكانة كبيرة في التاريخ الإسلامي كما ورد في القرآن على أنه المسجد الذي بني على \"التقوى\". كما أنه من المواقع التي لها فضل في زيارتها والصلاة فيها خضع للعديد من التوسعات ويستوعب حاليا لـ 20 ألف مصلِ، وأطلق ولي العهد الأمير محمد بن سلمان مشروع التوسعة الكبرى والتي تهدف إلى زيادة مساحة المسجد بما يوازي 10 أضعاف ورفع الطاقة الاستيعابية وتطوير المواقع التاريخية المحيطة', 3, 2, NULL, 'https://maps.app.goo.gl/8R7hJy1sD2ciyY379', 'https://www.visitsaudi.com/content/dam/saudi-tourism/media/al-madinah/masjid-quba-in-almadinah-new.jpg', 'Quba_Mosque.html', 'دينية'),
(382, 'مسجد القبلتين', 'يعتبر من أقدم المساجد، وسمي بهذا الاسم نسبة للحدث التاريخي عندما استدار النبي - صلى الله عليه وسلم – امتثالا لأمر الوحي نحو الكعبة. تم إطلاق مشروع مركز القبلتين الحضاري ليهتم بالبعد التاريخي وزيادة الطاقة الاستيعابية لمسجد القبلتين بالإضافة إلى إنشاء جسر مشاة، مواقف حافلات وسيارات، ساحات للأنشطة، مركز ثقافي ومركز معلومات، ومبنى معارض ومركز تجاري ويهدف مشروع مركز القبلتين إلى توسعة المسجد تماشيًا مع رؤية ٢٠٣٠', 3, 2, NULL, 'https://maps.app.goo.gl/9XidWE657mTHPdjW6', 'https://blog.wasalt.sa/wp-content/uploads/2024/07/%D9%85%D8%B3%D8%AC%D8%AF-%D8%A7%D9%84%D9%82%D8%A8%D9%84%D8%AA%D9%8A%D9%86-%D9%88%D8%A3%D9%87%D9%85-6-%D9%85%D8%B9%D9%84%D9%88%D9%85%D8%A7%D8%AA-%D8%AE%D8%A7%D8%B5%D8%A9-%D8%A8%D9%87.png', 'Qiblatain_Mosque.html', 'دينية'),
(383, 'منطقة الخندق (المساجد السبعة)', 'أحد أهم المزارات التي يقصدها زوار المدينة المنورة، حيث تعتبر هذه المنطقة هي مركز قيادة غزوة الخندق، ويوجد بها سبع مساجد تاريخية من أهمها مسجد الفتح الذي تم بناءه مؤخرا بالإضافة إلى عدد من المساجد التاريخية التي سميت بأسماء الصحابة المشاركين في حفر الخندق وتتسم هذه المساجد بصغر حجمها وتصميماتها الفريدة وهندستها المتنوعة', 3, 2, NULL, 'https://maps.app.goo.gl/HbnPhTnf3FujCNVf7', 'https://upload.wikimedia.org/wikipedia/commons/thumb/e/ec/Sab%27u_Masajid.jpg/1200px-Sab%27u_Masajid.jpg', 'The_Trench_area_(the_Seven_Mosques).html', 'دينية'),
(384, 'مسجد الميقات', 'يُعدّ الميقات من أبرز المواقع التاريخية في المدينة المنورة، وهو أحد المواقيت التي حدّدها الرسول - صلى الله عليه وسلم - لمن أراد الحج أو العمرة، وهي ميقات أهل المدينة ومن مرّ بها في الطريق إلى مكة. وبالنظر إلى المكانة التاريخية التي يحتلها مسجد الميقات يشهده أعداد كبيرة من المحرمين للحج والعمرة وقد تم تجميل المنطقة المحيطة به وتزويده بكافة المرافق اللازمة ليصبح الميقات محطة متكاملة للمسافرين', 3, 2, NULL, 'https://maps.app.goo.gl/5tafnDcZHM6F73Ek6', 'https://media.arabicradio.net/original/2022/07/06/637927174311985184.jpg', 'Miqat_Mosque.html', 'دينية'),
(385, 'مسجد الجمعة', 'تحتضن المدينة المنورة العديد من المساجد التاريخية المرتبطة بالسيرة النبوية العطرة، ومنها مسجد الجمعة الذي شهد موضعه أول صلاة جمعة لرسول الله صلى الله عليه وسلم بعد هجرته إلى المدينة المنورة. يتسع المسجد لأكثر من 650 مصلٍ ويحتوي قبة كبيرة كتب فيها سورة الجمعة بطريقة بديعة، وقد تم مؤخراً إعادة تأهيل وترميم المسجد ضمن برنامج إعمار المساجد التاريخية', 3, 2, NULL, 'https://maps.app.goo.gl/Be8MsrYoXjNUaPug8', 'https://experiencemedina.com/wp-content/uploads/2024/02/photo1709128212.jpeg', 'Friday_Mosque.html', 'دينية'),
(386, 'مسجد الإجابة', 'مسجد تاريخي عتيق بني في عهد النبي صلى الله عليه وسلم، سُمِّي بمسجد الإجابة لاستجابة الله تعالى لدعاء الرسول صلى الله عليه وسلم فيه، وقد تم تسجيل المسجد ضمن مشروع إعمار المساجد التاريخية لتأهيله وتحسينه وتعزيز هويته التاريخية', 3, 2, NULL, 'https://maps.app.goo.gl/awQDMyAXgsiDzmwV9', 'https://upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Masjid_Ijabah_Imam_Khairul_Annas.JPG/1280px-Masjid_Ijabah_Imam_Khairul_Annas.JPG', 'Al_Ijaba_Mosque.html', 'دينية'),
(387, 'مسجد السُقيا', 'بُني مسجد السقيا في مكان قبّة الرسول صلى الله عليه وسلم عند خروجه لغزوة بدر وقد صلّى الرسول صلى الله عليه وسلم في هذا المكان ودعا لأهل المدينة بالبركة، ويرجع سبب تسميه مسجد السقيا بهذا الاسم لوقوعه قرب (بئر السقيا). ويقع مسجد السقيا في المدينة المنورة داخل سور محطة السكة الحديد، ويقابل حاليا مبنى أمانة المدينة المنورة', 3, 2, NULL, 'https://maps.app.goo.gl/6qLgykuxv3ZbtBFKA', 'https://upload.wikimedia.org/wikipedia/commons/2/29/%D9%85%D8%B3%D8%AC%D8%AF_%D8%A7%D9%84%D8%B3%D9%82%D9%8A%D8%A7.jpg', 'Al_Suqya_Mosque.html', 'دينية'),
(388, 'مسجد الغمامة', 'يقع في الجهة الغربية الجنوبية للمسجد النبوي على بعد 500 متر من باب السلام، وكان آخر المواضع التي صلى بها الرسول محمد صلى الله عليه وسلم صلاة العيدين وصلاة الاستسقاء، وسمي بالغمامة لما يقال أن غمامة حجبت الشمس عن رسول الله صلى الله عليه وسلم عند صلاته فيه', 3, 2, NULL, 'https://maps.app.goo.gl/eySGFbLfmWhsFpK1A', 'https://scth.scene7.com/is/image/scth/masjid-alghamamah-in-almadinah-2:crop-760x570?defaultImage=masjid-alghamamah-in-almadinah-2', 'Al_Ghamama_Mosque.html', 'دينية'),
(389, 'مسجد السجدة', 'مسجد السجدة هو أحد المساجد المجاورة والقريبة من المسجد النبوي الشريف، سمي بمسجد السجدة لسجود الرسول صلى الله عليه وسلم فيه سجدة طويلة، ويقال له مسجد الشكر لسجود النبي فيه سجدة الشكر، ويقال عنه مسجد البحيري لوقوعه عند بستان النخيل الذي عرف بالبحير', 3, 2, NULL, 'https://maps.app.goo.gl/Sxyqqga1LJrXSYRe7', 'https://upload.wikimedia.org/wikipedia/commons/8/82/Abu_Thar_al_Gafari_Mosque_1.JPG', 'Al_Sajdah_Mosque.html', 'دينية'),
(390, 'جبل أحد', 'يعد جبل أحد أكبر وأبرز جبال المدينة المنورة والذي شاء الله تعالى أن يجعله من خيرة جبال الأرض وفضَّلَه وجعله من جبال الجنة، يحرص القادمون إلى المدينة المنورة على زيارة جبل أحد لكونه من المعالم الأثرية التاريخية التي شهدت أحداثاً تاريخية عظيمة أهمها غزوة أحد وقد تم إطلاق مشروع \"جادة أحد\" بهدف تطوير الطريق الرابط بين المسجد النبوي الشريف وميدان سيد الشهداء وجبل أحد', 3, 2, NULL, 'https://maps.app.goo.gl/xLgs8dDvT6jGFXmd8', 'https://www.urtrips.com/wp-content/uploads/2022/11/Uhud-Mountain-Medina2-1.jpg', 'Mount_Uhud.html', 'تراثية'),
(391, 'ساحة شهداء أحد وتل الرماة', 'أحد أهم المعالم التاريخية التي يحرص الزوار على زيارتها، إذ شهد المكان معركة أحد بين جيشي المسلمين والكفار، وتحوي مقبرة الشهداء أجساد 70 صحابياً استشهدوا في المعركة الخالدة أشهرهم سيدنا حمزة بن عبد المطلب عم الرسول صلى الله عليه وسلم (سيد الشهداء). ومن أهم معالم الموقع جامع \"سيد الشهداء\" الذي شيد بطراز معماري متميز على مساحة تزيد على 54 ألف متر مربع، ليستوعب نحو 15 ألف مصلٍ، ويحوي مختلف الخدمات والمرافق المساندة تشمل ساحات محيطة لوقوف السيارات, ويقع قبالة \"جبل الرماة\" الذي يعدّ معلماً بارزاً دارت في قمته ومحيطه أحداث المعركة.', 3, 2, NULL, 'https://maps.app.goo.gl/NPHG8w8ZSoasxKjp6', 'https://www.alyaum.com/uploads/images/2018/08/06/423014.jpg', 'Martyrs\'_Square_of_Uhud_and_Archers\'_Hill.html', 'تراثية'),
(392, 'حرة رهط', 'تعد حرَّة رهط ضمن 12 حقلاً بركانياً، تتوزع في المملكة. وتضم الحرَّة وحدها سبعمائة مخروط بركاني، كما أنه تم تصنيف الحرَّة ضمن مشروع «حماية المعالم الجيولوجية»، بوصفه أول متنزه جيولوجي مفتوح في السعودية من قِبل الهيئة العامة للسياحة والتراث الوطني', 3, 2, NULL, 'https://maps.app.goo.gl/hNpWVzwGDxjXQgrm7', 'https://th.bing.com/th/id/R.5896a3be8b0d2aeaf42cf37788c42d42?rik=jWhxlOpMrZ8Azg&riu=http%3a%2f%2fwww.alweeam.com.sa%2fwp-content%2fuploads%2f2016%2f05%2f07.jpg&ehk=50mWKSuZfWMSrEVEdEBg5YghAG4U%2bQ3FhD0zUEYbYwg%3d&risl=&pid=ImgRaw&r=0', 'Free_Rahat.html', 'سياحية'),
(393, 'وادي العقيق', 'من أشهر أودية المدينة المنورة اكتسب شهرته لارتباطه بسيرة الرسول صلى الله عليه وسلم والذي سمي بالوادي المبارك، تدل الكتابات التاريخية أن وادي العقيق كان في بعض العصور أشبه بنهر دائم الجريان. وقد تم إطلاق مشروع التأهيل البيئي لوادي العقيق الذي تقوم عليه هيئة تطوير منطقة المدينة المنورة وهو مشروع استراتيجي يستوعب جميع مقومات وادي العقيق من حيث المحافظة على بيئته الطبيعية إلى جانب تطويره وتأهيله لتحقيق التوازن المنشود في الاستفادة من مقوماته', 3, 2, NULL, 'https://maps.app.goo.gl/9KHkUqrE3fg37ZES8', 'https://pbs.twimg.com/media/EfYkZ2JX0AAYgM8.jpg', 'Wadi_Al_Aqiq.html', 'تراثية'),
(394, 'جبل الفقرة', 'الفقرة عبارة عن سلسلة جبال متجاورة في خط طولي تقريبًا تشبه تماماً فقرات العمود الفقري وهذا ما يرجح سبب تسميتها الفقرة، وترتفع الفقرة عن سطح البحر بحوالي 1,901م، وهي منطقة معتدلة في شدة الصيف وباردة جداً في فصل الشتاء لذا أصبحت منطقة جذب لأهالي المدينة المنورة وزوّارها وزادت الحركة السياحية فيها. وتجذب السياحة في الفقرة عدسات المصورين والهواة لالتقاط مناظر بديعة لكتل السحب وهي تعانق قمم جبالها', 3, 2, NULL, 'https://maps.app.goo.gl/YfcV711xogMQmwYKA', 'https://cnn-arabic-images.cnn.io/cloudinary/image/upload/w_1920,c_scale,q_auto/cnnarabic/2020/12/31/images/173679.jpg', 'Mount_Para.html', 'سياحية'),
(395, 'متحف دار المدينة', 'متحف دار المدينة للتراث العمراني والحضاري هو أول متحف متخصص لعرض التاريخ الحضاري والثقافي والإسلامي للمدينة المنورة، وإظهار معالم الحقبة التاريخية التي عاش بها الرسول محمد صلى الله عليه وسلم من خلال عرض تفاصيل من معالم السيرة النبوية والإسلامية والثقافة العمرانية والحضارية للمدينة المنورة', 3, 2, NULL, 'https://maps.app.goo.gl/JGJfaMK5bKhxPsVK7', 'https://axrzaord1cno.compat.objectstorage.me-jeddah-1.oraclecloud.com/new_assets/Areas/26122023020905333.webp', 'City_House_Museum.html', 'تراثية'),
(396, 'معرض القرآن الكريم', 'يقع معرض القرآن الكريم في واجهة القبة الخضراء أمام باب السلام المؤدي إلى الروضة الشريفة على مساحة 1500 متر مربع. ويهدف المعرض إلى تعريف الناس بكتاب الله تعالى عبر محتوى دقيق ومنظومة واسعة من التقنيات وفق أسلوب العرض المتحفي وإبراز جهود الدولة السعودية التاريخية والحديثة في العناية بالقرآن الكريم.', 3, 2, NULL, 'https://maps.app.goo.gl/eUac4kfUZTLZPUtb7', 'https://www.spa.gov.sa/_next/image?url=https%3A%2F%2Fportalcdn.spa.gov.sa%2Fbackend%2Foriginal%2F202303%2Frr2NZ038LwupCt9F59Me9yY91Sgw1rb6UC2NSGNO.jpg&w=3840&q=75', 'Holy_Quran_Exhibition.html', 'تراثية'),
(397, 'معرض أسماء الله الحسنى', 'معرض أسماء الله الحسنى هو معرض دائم مقام في المدينة المنورة غرب المسجد النبوي الشريف، ويقدم المعرض عرضا لأسماء الله الحسنى ويدعو زواره إلى التأمل والتفكر في أسمائه سبحانه عبر منظومة من اللوحات وتقنيات العرض الحديثة لتعزز الإيمان بالله تعالى وتجعل الزائر ينطق بتسبيحه وتمجيده', 3, 2, NULL, 'https://maps.app.goo.gl/ibHzrbGMVN23hgAF8', 'https://samayainvest.com/wp-content/uploads/2023/03/1-scaled.jpg', 'Exhibition_of_the_Beautiful_Names_of_God.html', 'دينية'),
(398, 'المتحف والمعرض الدولي للسيرة النبوية والحضارة الإسلامية', 'يُعنى المتحف الدولي للسيرة النبوية بالتعريف الحضاري الشامل بالنبي صلى الله عليه وسلم عبر أحدث التقنيات تحت إشراف رابطة العالم الإسلامي، وتمثل محتويات ومقتنيات المعرض إرثاً تاريخياً تنطلق منه لنشر المنهاج النبوي القويم وسيرة المصطفى العطرة لكل أرجاء العالم، لتبيان سماحة الدين الحنيف ووسطيته ويضم 25 جناحاً رئيسياً لمشاهدة معالم حياة النبي صلى الله عليه وسلم، والتعريف بجميع الأنبياء', 3, 2, NULL, 'https://maps.app.goo.gl/S3gdmPEYjfzStokBA', 'https://nasiralzahrani.sa/wp-content/uploads/2022/05/1F1B7789-1-copy-min-scaled.jpg', 'Museum_of_the_Prophet\'s_Biography.html', 'تراثية'),
(399, 'محطه سكة حديد الحجاز', 'تقع محطة سكة حديد الحجاز التاريخية بالمدينة المنورة غرب المسجد النبوي الشريف، والتي تم إنشاؤها في عام 1908م لتسريع سفر الحجاج المسلمين من دمشق إلى مدينتي مكة المكرمة والمدينة المنورة المقدستين تم تحويل المحطة إلى متحف أثري في عام 1419 هـ وفتح المتحف أبوابه لجميع الزائرين من كل مكان من أجل الاستمتاع بكل ما يحتويه من كنوز من تلك الحقبة الزمنية', 3, 2, NULL, 'https://maps.app.goo.gl/Eja4JPsrZQDcDPUi7', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSweSKGtf6uwBEAQDLFEBHL5al0h6518iZfpQ&s', 'Hejaz_Railway_Station.html', 'تراثية'),
(400, 'متحف مكة', 'يقع متحف مكة في قصر الزاهر التاريخي، الذي كان في السابق منزلًا ملكيًا، ويضم معروضات تحكي تاريخ مكة، وعلم الآثار السعودية، وقصة ظهور الإسلام', 1, 1, NULL, 'https://maps.app.goo.gl/MT45Bv1piud6rWG4A', 'https://mabruktour.com/wp-content/uploads/2024/08/2-1651062321.webp', 'Makkah_Museum.html', 'تراثية'),
(401, 'جزيرة السفن المحطمة', 'إلى الجنوب الغربي من مدينة جدة ، سوف تجد جزيرة أبو طير، حيث ستكتشف حطام أشهر السفن في العالم،وهو كل ما تبقى من السفينة اليونانية ستافونوس التي غرقت عام 1978م.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=20.867164,39.361457', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-16-19/01.jpg.thumb.1200.1200.jpg', 'jeddah_shipwreck_island.html', 'تراثية'),
(402, 'مزرعة مشعل السفياني للورد', 'تشتهر مزرعة مشعل السفياني للورد، بإدارتها العائلية، بتقطيرها التقليدي وحقولها الواسعة من ورد الطائف. استمتع بمشاهدة عملية استخراج ماء الورد، وتمشّ بين الأزهار المتفتحة، وخذ معك زجاجات من العطور المحلية. رحلة حسية في قلب تقاليد المملكة.', 1, 4, NULL, 'https://www.google.com/maps/search/?api=1&query=21.077281364249526,40.398688863684995', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/taif_2025/mishaal-al-sufyan-rose-farm/DSC_9509.jpg.thumb.1200.1210.jpg', 'mishaal_al_sufyani_rose_farm_taif.html', 'طبيعية'),
(403, 'منتجع الغزال', 'من اللحظة الأولى لدخولك المنتجع، ستشعر بأنك انتقلت إلى عالم من الهدوء والذوق الرفيع، فتفاصيل المكان هنا ليست مجرد ديكور، بل تجربة حسية متكاملة؛ من التصاميم الداخلية التي تبعث على الراحة وتحتفي بالهوية المحلية بروح عصرية، إلى مياه الزير التي تنساب بهدوء في الممرات، مانحة المكان بعدًا تراثيًا يعانق الطبيعة.', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B003\'34.1%22N+40%C2%B020\'12.2%22E/@21.059465,40.3367206,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.059465!4d40.3367206?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/taif_2025/gazella-resort/DSC_8586.jpg.thumb.1200.1210.jpg', 'ghazal_resort_taif.html', 'طبيعية'),
(404, 'جبل دكا', 'يطل جبل دكا على مركز الشفا السياحي بمنطقة الطائف، ويستخدم الجبل لمعرفة مواعيد الصلاة والصيام والإفطار في شهر رمضان. يتسم الجبل بكثافة وتنوع الغطاء النباتي وتكسوه أشجار العرعر. تصل درجة الحرارة في قمة الجبل إلى ما دون الصفر المئوي في فصل الشتاء، لذلك يعد من الوجهات المميزة، ومنتجعًا سياحيًّا يلبي تطلعاتك.', 1, 4, NULL, 'https://www.google.com/maps/search/?api=1&query=21.09429719,40.29489164', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-8/%d8%b5%d9%88%d8%b1%d8%a9%20%d9%a2%d9%a0%d9%a2%d9%a2-%d9%a1%d9%a0-%d9%a1%d9%a2%20%d9%a0%d9%a2.%d9%a2%d9%a2.%d9%a5%d9%a0%20%d8%b5.jpg.thumb.1200.1200.jpg/', 'jabal_dakka_taif.html', 'طبيعية'),
(405, 'جبال الشفا', 'على قمم جبال الشفا استمتع بالجلوس وسط المسطحات الخضراء وأشجار العرعر، كما يمكنك اكتشاف وجهات عائلية رائعة تشمل مقاهيَ ومطاعم ومعالم طبيعية توفر أنشطة شيقة مثل ركوب الدراجات النارية وتسلق الجبال.', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B005\'11.1%22N+40%C2%B017\'05.2%22E/@21.0864214,40.2847858,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.0864214!4d40.2847858?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-6/DJI_0764.jpg.thumb.1200.1200.jpg/', 'shafa_mountains_taif.html', 'طبيعية'),
(406, 'حديقة الردف', 'تقع حديقة الردف في قلب مدينة الطائف، وتتميز الحديقة بطبيعتها الملونة وبركة اصطناعية ونافورة راقصة، فضلًا عن منحوتات عشبية تعطي الزوار تجربة ساحرة للطبيعة.', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B013\'45.2%22N+40%C2%B025\'31.0%22E/@21.2292183,40.4252643,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.2292183!4d40.4252643?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-11/DJI_0841.jpg.thumb.1200.1200.jpg/', 'al_raddaf_park_taif.html', 'طبيعية'),
(407, 'حديقة حيوانات الطائف', 'استكشف عالم الحيوانات الأليفة عن قرب في حديقة الحيوانات بالطائف، حيث يدهش أطفالك تنوّع الحيوانات وأحجامها المتباينة في رحلة ملؤها المتعة', 1, 4, NULL, 'https://www.google.com/maps/search/?api=1&query=21.23069112,40.42771029', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-15/IMG_2271.JPG.thumb.1200.1200.jpg/', 'taif_zoo.html', 'طبيعية'),
(408, 'قصر الكعكي', 'يسافر بك قصر الكعكي بالطائف إلى العهدين الروماني والعثماني، إذ شيّد بطرز معماريّة رومانية وعثمانية وزخارف بديعة التكوين', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B016\'11.1%22N+40%C2%B024\'19.8%22E/@21.269735,40.405501,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.269735!4d40.405501?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-3/IMG_2649.JPG.thumb.1200.1200.jpg/', 'al_kaaki_palace_taif.html', 'تراثية'),
(409, 'سوق البلد', 'تجربة مميزة للتسوق في سوق البلد بالطائف أحد أشهر الأسواق التاريخية بمنتجاته المتنوعة من ملابس شعبية وبهارات وسوق خاص بالسمك لمحبي شراء الأسماك الطازجة.', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B016\'18.7%22N+40%C2%B024\'22.5%22E/@21.2718657,40.4062528,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.2718657!4d40.4062528?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-22/IMG_2660.JPG.thumb.1200.1200.jpg/', 'souq_al_balad_taif.html', 'سياحية'),
(410, 'البلد الطائف', 'البلد الطائف هو حي نابض بالحياة يعجّ بالأزقة الضيقة والمباني التراثية والأسواق التقليدية. تحوي المنطقة على عمارة حجازية ومساجد قديمة ومحلات عطور وتوابل وحِرف يدوية. إنه متحف حي حيث يلتقي الماضي بالحاضر على فنجان قهوة سعودية.', 1, 4, NULL, 'https://www.google.com/maps/search/?api=1&query=21.275655904096737,40.40555586954877', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/taif_2025/albalad-taif/DSC_8755-Enhanced-NR.jpg.thumb.1200.1210.jpg', 'al_balad_taif.html', 'تراثية'),
(411, 'متحف قصر شبرا', 'متحف قصر شبرا، هو أحد القصور التاريخية التي سكنتها العائلة المالكة قبل افتتاحه متحفاً للزوار عام ١٩٩٥، يتميز المتحف بطرازه المعماري الفريد، الذي يمزج بين الطراز الروماني والإسلامي، بالإضافة للطراز الحجازي التقليدي، يتكون المتحف من ٤ طوابق ب١٥٠ غرفة بمعروضاتها الأثرية، بالإضافة إلى الحدائق المحيطة به.', 1, 4, NULL, 'https://www.google.com/maps/search/?api=1&query=21.28640212450243,40.41505742511857', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/taif/shubra-palace-museum.jpg.thumb.1200.1210.jpg', 'shubra_palace_museum_taif.html', 'تراثية'),
(412, 'سيتي ووك الطائف', 'سيتي ووك الطائف ممشى عصري يجمع بين المطاعم والمقاهي والمتاجر والفعاليات الموسمية. ينبض بالحياة ليلاً بالأضواء والموسيقى وعربات الطعام، ما يجعله وجهة مفضلة لعطلة نهاية الأسبوع.', 1, 4, NULL, 'https://www.google.com/maps/search/?api=1&query=21.290908244800335,40.43396700782354', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/taif_2025/taif-city-walk/taif-city-walk_01.png.thumb.1200.1210.jpg', 'citywalk_taif.html', 'ترفيهية'),
(413, 'قصر جبرة التاريخي', 'يعود بناء قصر جبرة لما قبل 1362 عامًا تقريبًا، ويتميز القصربنمط التصاميم الإسلامية، إذ يضم ثلاثة طوابق ومدخلًا مزينًا بالزخارف الجميلة، وتتوسط باحة القصر نافورة ماء، فيما تحكي ردهاته تفاصيل التاريخ العريق الممتد على مر السنين.', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B018\'06.9%22N+40%C2%B026\'19.5%22E/@21.301915,40.43874,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.301915!4d40.43874?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-1/DJI_0862.jpg.thumb.1200.1200.jpg/', 'jubra_historic_palace_taif.html', 'تراثية'),
(414, 'حديقة الفيصلية', 'متعة التجمع العائلي في حديقة الفيصلية بالطائف، إذ تجمع ما بين الطبيعة والأشجار الظليلة والنوافير المائية، ومناطق ترفيهيّة تسعد الأطفال، ومناطق للشواء برفقة من تحب', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B018\'06.6%22N+40%C2%B025\'25.3%22E/@21.3018373,40.423697,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.3018373!4d40.423697?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-14/IMG_2696.JPG.thumb.1200.1200.jpg/', 'alfaisaliah_park_taif.html', 'طبيعية'),
(415, 'مصنع الشيوخ', 'مصنع الشيوخ عبارة عن معرض لمشتقات الورد الطائفي من دهن الورد وماء الورد وماء العروس، إضافة إلى ما يتيحه من تأمل الورد الطائفي أثناء احتساء القهوة وتناول المشروبات والحلويات في مقهى مصنع الشيوخ.', 1, 4, NULL, 'https://www.google.com/maps/search/?api=1&query=21.34791496,40.32501778', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-17/IMG_6837.jpg.thumb.1200.1200.jpg/', 'al_shuyoukh_factory_taif.html', 'طبيعية'),
(416, 'واحة الطيور', 'تعتبر واحة الطيور من أجمل الأماكن التي تستقطب محبي الطيور والسياح الراغبين بخوض تجربة جديدة، حيث تمتد حديقة واحة الطيور على مساحة 800 متر وتحتوي على 400 طائر بأنواعها المختلفة والنادرة.', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B020\'28.3%22N+40%C2%B017\'30.7%22E/@21.3412057,40.2918618,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.3412057!4d40.2918618?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-16/IMG_6936.jpg.thumb.1200.1200.jpg/', 'bird_oasis_taif.html', 'طبيعية'),
(417, 'مزرعة الفراولة', 'تقع مزرعة الفراولة في منطقة الهدا الخصبة، وتقدّم تجربة ممتعة للعائلات وعشّاق الطعام. تجوّل بين الصفوف الحمراء، وتذوّق العصائر والمربى والحلويات الطازجة. وجهة موسمية مفضّلة ومتنفس زراعي منعش في قلب الطائف.', 1, 4, NULL, 'https://www.google.com/maps/search/?api=1&query=21.355414469225625,40.271470083814165', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/taif_2025/strawberries-farm/DSC_9291.jpg.thumb.1200.1210.jpg', 'strawberry_farm_taif.html', 'طبيعية'),
(418, 'تلفريك الهدا', 'حلّق فوق جبال الكرّ داخل عربة تلفريك الهدا، حيث ترافقك الإطلالات الجبليّة البديعة أثناء نزولك من فندق رمادا في أعلى جبال الهدا وصولًا إلى قرية الكر السياحيّة أسفل الجبل.', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B021\'50.6%22N+40%C2%B016\'01.8%22E/@21.3640431,40.2671629,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.3640431!4d40.2671629?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-14-58/01.jpg.thumb.1200.1200.jpg/', 'al_hada_cable_car_taif.html', 'ترفيهية'),
(419, 'جبل الهدا', 'يقف جبل الهدا شامخًا بين الغيوم الكثيفة في مدينة الطائف، ويقدم لزوّاره مغامرات جبلية فريدة تبدأ بركوب التلفريك وتنتهي بالجلوس وسط تكويناته الصخريّة', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B022\'17.0%22N+40%C2%B015\'29.3%22E/@21.3713802,40.2581451,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.3713802!4d40.2581451?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-7/DJI_0721.jpg.thumb.1200.1200.jpg/', 'jabal_al_hada_taif.html', 'طبيعية'),
(420, 'قرية الكر السياحية', 'قرية الكر السياحية عبارة عن قرية ترفيهية تحيط بها مسارات تاريخية تمكن السائح من النظر إلى طريق القوافل القديم، وتضم فعاليات ترفيهية متنوعة من الألعاب المائية ونسيج الأمواج والزلاجات السريعة وتجربة ركوب التلفريك، كما يتوفر بها العديد من المطاعم والمحلات التجارية.', 1, 4, NULL, 'https://www.google.com/maps/place/21%C2%B021\'25.3%22N+40%C2%B013\'41.8%22E/@21.3570341,40.2282636,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.3570341!4d40.2282636?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-atf-11-29/DJI_0709.jpg.thumb.1200.1200.jpg/', 'al_kar_village_taif.html', 'سياحية'),
(421, 'عين زبيدة', 'تمثّل عين زبيدة إرثًا حضاريًا هندسيًا في أرض مكة المكرمة، يبرز الاهتمام بسقاية حجاج بيت الله الحرام منذ القدم، فهي أقدم وأهم شبكة مائية ظلت تسقي الحجاج والمسافرين وأهالي مكة على مدى 1200 عام.', 1, 1, NULL, 'https://www.google.com/maps/place/21%C2%B020\'03.2%22N+40%C2%B002\'59.5%22E/@21.3342121,40.0498712,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.3342121!4d40.0498712?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/makkah/ain-zubaida-in-makkah.jpg.thumb.1200.1210.jpg', 'ain_zubaida_makkah.html', 'تراثية'),
(422, 'جبل الرحمة', 'جبل صغير بالنسبة لما حوله من الجبال، لا يزيد ارتفاعه عن ثلاثين مترا ٌ ، وفي أعلاه شاخص ُ ُمحدث بني َ ليكون علما يستدل به عليه بارتفاع أربعة أمتار؛ ويقع إلى الشمال من سهل عرفات َ وخارج حدود أعلام الحرم، وهو جبل صعب المرتقى لذلك بنيت ٌ فيه درجات توصلك إلى أعلاه بلغت أكثر من 91 درجة. ُ وقد غلب عليه اسم \"جبل الرحمة\"، وهكذا عرفه المسلمون إيمانا ً بما وعد الله عزو جل عباده المخلصين من الحجاج ُ بالغفران والرضوان والرحمة؛ أما اسمه القديم قبل الإسلام فهو \"جبل ألال\"', 1, 1, NULL, 'https://www.google.com/maps/place/21%C2%B021\'17.4%22N+39%C2%B059\'02.4%22E/@21.354843,39.9839914,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.354843!4d39.9839914?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/rcmc/Jabal_Al-Rahmah.png.thumb.1200.1210.jpg', 'jabal_al_rahmah_makkah.html', 'طبيعية'),
(423, 'مسجد نمرة', 'يقع هذا المسجد شمالي عرفات؛ وسمي بمسجد \"نمرة\" نسبة إلى جبل صغير غربي المسجد يسمى \"نمرة\"، ويُعرف المسجد أيضاً بمسجد \"إبراهيم الخليل\"، كما يعرف باسم مسجد \"عرفة\" ومسجد \"عرنة\". بُني المسجد على الأرجح في منتصف القرن الثاني الهجري، وشهد عدة تجديداتٍ وإصلاحات خلال العصر الإسلامي، ويُصلى في هذا المسجد جموع الحجيج يوم عرفة صلاة الظهر والعصر جمعاً وقصراً.', 1, 1, NULL, 'https://www.google.com/maps/search/?api=1&query=21.3529694,39.96630467', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/rcmc/Masjid_Namirah.jpg.thumb.1200.1210.jpg', 'namirah_mosque_makkah.html', 'دينية'),
(424, 'معهد المخطوطات وإحياء التراث الإسلامي', 'استطـــاع المركــز أن يجمــع أمهـــات المخطوطــــــات الإسلامية مــــــن شتى بقــاع العالـــم، واجتمــع لديــــــه في زمـن مبكـــر أكثــر من عشريـــن ألــــــف مخطوطـــــة خدمــــت أعدادًا لا تحصى من أسـاتذة الجامعـات السعوديـــة والعربية والعالمية وطلاب الدراسـات العليــا في الـداخـــل والخــــارج، وتشمل اختصاصــــاته علـــوم الدراســـــــات الإسلامية واللغة العربيـة والدراسـات التاريخيـــــة والحضاريــــة،​ بالإضافة إلى وظيفتــــــه الأساسيـة في إحيــــاء التـــراث الإسلامي.', 1, 1, NULL, 'https://www.google.com/maps/place/21%C2%B019\'44.8%22N+39%C2%B057\'13.4%22E/@21.3291042,39.9537074,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.3291042!4d39.9537074?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/makkah/Institute-of-manuscripts-and-islamic-heritage.jpg.thumb.1200.1210.jpg', 'manuscripts_institute_makkah.html', 'ثقافية'),
(425, 'البلد', 'لا تفوّت فرصة التجوال بين أزقة الماضي الجميل في البلد بمنطقة جدة التاريخيّة المسجّلة في قائمة اليونسكو للتراث العالمي.', 1, 3, NULL, 'https://www.google.com/maps/place/21%C2%B029\'04.8%22N+39%C2%B011\'00.8%22E/@21.4846608,39.1835444,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.4846608!4d39.1835444?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://jeddahalbalad.sa/uploads/images/projects/historic-buildings/overview.webp', 'al_balad_jeddah.html', 'سياحية'),
(426, 'مسجد الشافعي', 'استكشف التراث العمراني في مسجد الشافعي التاريخي بجدة، إذ بنيت منارته في القرن السابع الهجري.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.486688967912887,39.18764298881862', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-14-57/01.jpg.thumb.1200.1200.jpg/', 'al_shafei_mosque_jeddah.html', 'دينية'),
(427, 'بيت باعشن', 'يعتبر بيت باعشن أكثر التزاماً بطراز العمارة التقليدية، فالواجهة الرئيسية للمنزل تواجه الشارع الرئيسي من الجهة الشرقية، ويوجد مجلس في الجانب المطل على الشارع، ومجلس آخر في الجهة الشمالية، وأما الواجهات الشرقية فتحتوي على الرواشين التي تكون السطح الخارجي وقطاعات منزلقة من الخشب الصلد من الداخل مهمته صد الأتربة والغبار التي تحملها الرياح القادمة من الشمال الشرقي، ويوجد ارتداد في الجدار الخارجي في القطاع الأوسط من المبنى أعلى المدخل بشكل شرفة كبيرة للنوم، ولزيادة الحركة الهوائية داخل المنزل مما يعمل على تبريده بواسطة التبخير.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.487045,39.18668', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-13-4/2.png.thumb.1200.1210.jpg', 'bait_baashen_jeddah.html', 'تراثية'),
(428, 'إعمار سكوير', 'ينفتح على ساحة عامة ومساحات خضراء، تتوسطه نافورة تفاعلية تحيطها مجموعة رائعة من المقاهي المختصة والمطاعم، ويعد مكانًا مثاليًا للتنزه وتذوق خيارات عدة للطعام المحلي والعالمي.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.5105215377306,39.202346386095', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-14-43/01.jpg.thumb.1200.1200.jpg/', 'emaar_square_jeddah.html', 'سياحية'),
(429, 'نافورة الملك فهد', 'في حال كُنت تخطط لقضاء فترة العصر في واجهة جدة البحرية، فلا تفوت على نفسك المتعة وابقَ حتى الغروب حيث تُضاء نافورة الملك فهد التي تُعد أطول نافورة في العالم بأكثر من 500 كشافٍ ضوئي عند غروب الشمس.', 1, 3, NULL, 'https://www.google.com/maps/place/21%C2%B031\'08.0%22N+39%C2%B009\'10.7%22E/@21.5188932,39.1529668,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.5188932!4d39.1529668?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-14-0/01.jpg.thumb.1200.1200.jpg/', 'king_fahd_fountain_jeddah.html', 'طبيعية'),
(430, 'متنزه المنحوتات', 'نسائم البحر الأحمر المنعشة، ومتعة جماليّة ترافقك أثناء نزهتك في منتزة المنحوتات بكورنيش جدة', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.534982598724167,39.157451200756874', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-16-22/01.jpg.thumb.1200.1200.jpg/', 'sculpture_park_jeddah.html', 'طبيعية'),
(431, 'متحف عبدالرؤوف خليل', 'أسس هذا المتحف عبد الرؤوف خليل عام 1996، وهو لا يعرض التراث الثقافي الإسلامي الغني للمدينة فحسب، بل يعرض أيضًا تاريخها القديم الذي يعود إلى 2500 عام. يتتبع الحضارات المختلفة التي سكنت المنطقة. يقع في وسط المدينة، ويضم مجموعة كبيرة من العناصر والتحف.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.580606,39.176594', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-13-6/01.jpg.thumb.1200.1200.jpg/', 'abdulraouf_khalil_museum_jeddah.html', 'ثقافية'),
(432, 'بوليفارد جدة', 'يعد بوليفارد جدة واحدًا من أفخم مراكز التسوق الموجودة في مدينة جدة حيث يمتاز بتصميمه العصري المميز وأناقة ساحاته وحدائقه الخضراء التي تتخللها مجموعة من النوافير الصغيرة والشلالات الصناعية مما يشجع الزوار على قضاء الوقت في مطاعمه ومقاهيه بعد الانتهاء من التسوق في المحال التجارية المنتشرة في جميع طوابقه.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.569962032984826,39.12458697311369', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-14-41/01.jpg.thumb.1200.1200.jpg/', 'jeddah_boulevard.html', 'ترفيهية'),
(433, 'فقيه أكواريوم', 'يعد فقيه أكواريوم وجهة للتعليم والترفيه من خلال عرض عجائب البيئة المائية في البحر الأحمر، وعجائب البحار والمحيطات الأخرى من أنحاء العالم. مع وجود أكثر من 200 نوع بما في ذلك أسماك القرش وأسماك الهامور، وأسماك النابليون، وفرس البحر، وموري، يهدف الأكواريوم إلى مواصلة الاستمرار في التوسع في الحياة البحرية وذلك بإدخال دراجون البحر الفريد لاحقًا في هذا العام. يمكن للعائلات أيضًا أن يستمتعوا بعرض الدلافين وكلاب البحر يوميًا، كما سيكون هناك فرصة متاحة للسباحة مع الدلافين في وقت لاحق من هذا العام.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.57278272,39.10961017', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-13-0/Fakieh_Aquarium_0ba704622f.jpg.thumb.1200.1200.jpg/', 'fakeeh_aquarium_jeddah.html', 'ترفيهية'),
(434, 'سماء الغربية للطيران', 'وسترن سكاي افييشن من أوائل مدارس تعليم رياضات الطيران في المملكة ، والأولى في المنطقة الغربية ، والتي تحتوي على بعض من أقدم وأكفأ الطيارين المدربين الذين وضعوا بصمة قوية في تاريخ الرياضات الجوية في الغرب. منطقة واحتضان العديد من البطولات المحلية في الرياضات الجوية المختلفة.', 1, 3, NULL, 'https://www.google.com/maps/place/21%C2%B037\'47.5%22N+39%C2%B006\'54.6%22E/@21.629858,39.115161,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.629858!4d39.115161?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-17-3/01.jpg.thumb.1200.1200.jpg', 'samaa_al_gharbia_aviation_jeddah.html', 'ترفيهية'),
(435, 'آرت بروميناد', 'سوف تستمتع بمساحة جديدة مليئة بالمفاجآت تسمى آرت بروميناد داخل واجهة جدة البحرية, تمتد إلى المارينا ، وهي منطقة سكنية هادئة أمام البحر.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.64011854964687,39.103233652043976', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-16-21/02.jpg.thumb.1200.1200.jpg/', 'art_promenade_jeddah.html', 'ترفيهية');
INSERT INTO `tourist_attractions` (`id`, `name`, `description`, `region_id`, `city_id`, `attraction_Priority`, `location`, `image_url`, `html_file`, `category`) VALUES
(436, 'مسجد الرحمة العائم', 'هذا البناء الفاتن ، من أكثر الرموز المميزة لمدينة جدة. تم بناء المسجد العائم عام 1985 على سطح مائي ، مع قواعد مغروسة تحت سطح البحر. تتميز بلونها الأبيض الناصع وقبتها الفيروزية التي ترسم مشهدًا رائعًا خاصة عندما تشرق الشمس عليها لتعكس جمال العمارة.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.65188806,39.10145964', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-13-1/jeddah_floating_mosque_6be305ce9c.jpg.thumb.1200.1200.jpg/', 'floating_mercy_mosque_jeddah.html', 'دينية'),
(437, 'نادي جدة لليخوت', 'في عروس البحر الأحمر،لا تنتهي المغامرات الجديدة والاستكشافات المذهلة. لهذا السبب تم تصميم نادي اليخوت،الذي يغطي مساحة 122000 متر مربع ، لاستقبال القوارب الدولية التي يزيد طولها عن 120 مترًا.', 1, 3, NULL, 'https://www.google.com/maps/place/21%C2%B040\'21.3%22N+39%C2%B006\'11.7%22E/@21.6725892,39.1032607,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.6725892!4d39.1032607?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-16-20/02.jpg.thumb.1200.1200.jpg/', 'jeddah_yacht_club.html', 'سياحية'),
(438, 'شرم أبحر', 'يعد شرم أبحر أحد أفضل وجهات الغواصين في جدة، يقع شمالي وسط جدة.', 1, 3, NULL, 'https://www.google.com/maps/place/21%C2%B044\'34.8%22N+39%C2%B007\'14.0%22E/@21.742991,39.120562,17z/data=!3m1!4b1!4m4!3m3!8m2!3d21.742991!4d39.120562?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-jdh-16-18/01.jpg.thumb.1200.1200.jpg/', 'sharm_abhur_jeddah.html', 'طبيعية'),
(439, 'مدينة الملك عبدالله الاقتصادية', 'تقع مدينة الملك عبد الله الاقتصادية على بعد 100 كم شمال جدة بمحاذاة ساحل البحر الأحمر وتتميز بكونها مدينة حديثة ووجهة سياحية واعدة.', 1, 5, NULL, 'https://www.google.com/maps/place/22%C2%B026\'21.4%22N+39%C2%B007\'30.5%22E/@22.4392853,39.1251365,17z/data=!3m1!4b1!4m4!3m3!8m2!3d22.4392853!4d39.1251365?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-kaec-14-54/01.jpg.thumb.1200.1200.jpg/', 'king_abdullah_economic_city.html', 'سياحية'),
(440, 'نادي الغولف برويال غرينز', 'يقع نادي الغولف رويال غرينز في قلب حي المروج، وهو أحد أهم وأرقى مناطق مدينة الملك عبدالله الاقتصادية، بإطلالتها المباشرة على شواطئ البحر الأحمر على الساحل الغربي للمملكة العربية السعودية وعلى بعد ١٠٠ كيلومتر شمال مدينة جدة، والتي تمتد على مساحة ١٨١ كيلومتر مربع، وهو الوجهة الأنسب للباحثين عن تحدي جديد في أرجاء ملعب غولف عالمي.', 1, 5, NULL, 'https://www.google.com/maps/search/?api=1&query=22.448998,39.097082', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-kaec-5-5/DSC02758-2.jpg.thumb.1200.1200.jpg/', 'royal_greens_golf_club_kac.html', 'ترفيهية'),
(441, 'سفاري دي-بوب', 'تقدم سفاري دي-بوب رحلات سفاري بقيادة مُرشد على متن الدبابات والسيارات الصحراوية لمدة ساعة تقريبا. في تجربة ممتعة ومثيرة برفقة الأصدقاء أو العائلة.', 1, 5, NULL, 'https://www.google.com/maps/place/22%C2%B024\'07.8%22N+39%C2%B005\'17.6%22E/@22.402152,39.088215,17z/data=!3m1!4b1!4m4!3m3!8m2!3d22.402152!4d39.088215?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-kaec-5-8/DSC02685-2.jpg.thumb.1200.1200.jpg/', 'd_pop_safari_kac.html', 'ترفيهية'),
(442, 'شاطئ يام', 'زيارة واحدة لمدينة الملك عبدالله الاقتصادية كفيلة بأن تضع ابتسامة على وجوه الأطفال والكبار سوياً؛ حيث تعتبر وجهة مثالية للمغامرين والمسافرين من رجال الأعمال والعائلات وكل من يرغب بالهروب لمدينة بها كل شيء. في شاطئ يام، يمكنك أن تستلقي على الرمال الذهبية وتستمتع بمشاهدة الأفق والأمواج الزرقاء تداعب أقدامك مع الاستجمام على صوت هدير البحر. ويمكن لعشاق الأنشطة البحرية أن يحظوا بفرصة مميزة لاستئجار زورق للتجديف أو لعب الكرة الطائرة على الشاطئ مع غروب الشمس. يقع شاطئ يام في حي البيلسان، مركز المرح وهو بمثابة مسبح طبيعي ضحل تحيط به الشعاب المرجانية ويلقى رواجًا بين هواة ركوب الأمواج شراعيًا والغطس، بالإضافة إلى العائلات التي تفضّل الاسترخاء تحت المظلات المصنوعة من القش.', 1, 5, NULL, 'https://www.google.com/maps/place/22%C2%B023\'57.7%22N+39%C2%B004\'39.7%22E/@22.399355,39.077694,17z/data=!3m1!4b1!4m4!3m3!8m2!3d22.399355!4d39.077694?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-kaec-5-30/01.jpg.thumb.1200.1200.jpg/', 'yam_beach_kac.html', 'طبيعية'),
(443, 'حديقة الفنون الخضراء', 'تمتدّ حديقة الفنون الخضراء على مساحة فسيحة تجمع ما بين الطبيعة الخلابة والأعمال الفنية الرائعة، وهي من الوجهات المُحببة لكل من سُكان المنطقة وزوّارها من الكبار والصغار على حد سواء، وذلك لما تتمتّع به من جمال ساحر وأجواء منعشة ومرافق متنوّعة، حيث تتزيّن بالنباتات والأشجار ذات الأشكال الإبداعية التي تصوّر الحيوانات كالعنكبوت والجمل وغيرها، إضافةً إلى أنها تضم مسارات المشي الطويلة وأماكن الجلوس والمناطق المُخصصة لتناول الطعام.', 1, 5, NULL, 'https://www.google.com/maps/place/22%C2%B024\'01.7%22N+39%C2%B004\'42.9%22E/@22.400465,39.078573,17z/data=!3m1!4b1!4m4!3m3!8m2!3d22.400465!4d39.078573?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-kaec-5-2/DSC05339.jpg.thumb.1200.1200.jpg/', 'green_arts_park_kac.html', 'طبيعية'),
(444, 'منطقة الفن', 'لن تكتمل زيارتك إلى مدينة الملك عبدالله الاقتصادية دون الاستمتاع بجولة سيرًا على الأقدام واكتشاف منطقة الفن المليئة بجداريات الجرافيتي والرسم الثلاثي الأبعاد والكاريكاتيري وغيره من الفنون بأيدي الفنانين المحليين الموهوبين. تحتضن منطقة الفن العديد من الفعاليات والأنشطة الترفيهية التي تجذب العديد من محبي الفن. هذه الوجهة العصرية توفر إطلالات خلابة على البحر الأحمر وتضم العشرات من المطاعم الفاخرة والمقاهي للاستجمام والتأمل بالفن والطبيعة المحيطة.', 1, 5, NULL, 'https://www.google.com/maps/search/?api=1&query=22.402741,39.080458', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-kaec-5-4/DSC05353.jpg.thumb.1200.1200.jpg/', 'art_district_kac.html', 'سياحية'),
(445, 'موقع معركة بدر', 'وقعت معركة بدر فى منطقة بدر، في الجنوب الغربي من المدينة المنورة، وما زال موقع المعركة يضم عددا من الشواهد الأثرية.', 3, 6, NULL, 'https://www.google.com/maps/search/?api=1&query=23.774562,38.794021', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-bdr-10-8/IMG_0587.jpg.thumb.1200.1200.jpg/', 'battle_of_badr_site.html', 'دينية'),
(446, 'شاطىء النورس', 'شاطئ النورس هو ينبع يمتاز بالرمال الناعمة، وهو ما يسمح لكم بنصب الخيام، والجلوس فيها للاحتماء من حرارة الشمس وتناول الأكلات والمشروبات المُفضلة لديكم، والجلوس بها حتى الغروب للاستمتاع بمنظر قرص الشمس أثناء احتضان مياه البحر، يُمكنكم أخذ جولة من المشي في الجزيرة، والاستمتاع بالمناظر الطبيعية على شاطئ البحر الأحمر، أو الجلوس للاسترخاء على المقاعد المنتشرة على الشاطئ في مواجهة مياه البحر.', 3, 7, NULL, 'https://www.google.com/maps/place/24%C2%B000\'23.0%22N+38%C2%B009\'23.5%22E/@24.006399,38.156523,17z/data=!3m1!4b1!4m4!3m3!8m2!3d24.006399!4d38.156523?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/yanbu/al-nawras-beach-in-yanbu.jpg.thumb.1200.1210.jpg', 'al_nawras_beach_yanbu.html', 'طبيعية'),
(447, 'ينبع التاريخية', 'إذا كنت من محبي الأجواء التراثية والشعبية، نوصي بزيارة استثنائية إلى ينبع التاريخية، التي يبلغ عمرها نحو 2500 عام، حيث يمكن أخذ جولة مفعمة بالدهشة والمتعة.', 3, 7, NULL, 'https://www.google.com/maps/search/?api=1&query=24.0792514,38.0623574', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ynb-17-6/03.jpg.thumb.1200.1200.jpg', 'historic_yanbu.html', 'تراثية'),
(448, 'شاطئ ينبع البحر', 'يعد شاطئ ينبع البحر أحد أفضل أماكن التنزه للعائلات والأفراد في ينبع، حيث الاستمتاع بالطبيعة الخلابة وممارسة العديد من الأنشطة الشاطئية، يستمتع الرواد بالتريض على الشاطئ، وممارسة السباحة في الأماكن المخصصة لذلك، أيضا يستمتع الرواد بالخدمات الممتازة على الشاطئ خصوصا الاستراحات العائلية الموزعة بترتيب منظم وجميل، يستمتع الزوار أيضا بتناول الوجبات المميزة في المطاعم القريبة من المكان، وكذلك أكشاك المرطبات والعصائر، يمكن تناول القهوة والشاي وباقي المشروبات في المقاهي المنتشرة حول المكان.', 3, 7, NULL, 'https://www.google.com/maps/search/?api=1&query=24.114974,37.979056', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/yanbu/yanbu-sea-beach.jpg.thumb.1200.1210.jpg', 'yanbu_sea_beach.html', 'طبيعية'),
(449, 'حصن خيبر', 'لا تزال آثار حصن خيبر، الواقعة على قمة جبل القموص، قائمة منذ أكثر من 1400 عام عندما اقتحمه المسلمين بعد نقض اليهود للعهد مع الرسول صلى الله عليه وسلم، وهو شاهد على غزوة خيبر التي انتهت لصالح المسلمين، وكان من أقوى الحصون في ذلك الوقت؛ وهو الحصن الوحيد الذي حافظ على بعض معالمه إلى يومنا هذا.', 3, 8, NULL, 'https://www.google.com/maps/search/?api=1&query=25.730751,39.268257', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-khb-16-54/01.jpg.thumb.1200.1200.jpg/', 'khaybar_fort.html', 'دينية'),
(450, 'قلعة العلا', 'عادة ما يطلق عليها اسم \"حصن العلا\"، وهي تعود إلى القرن السادس قبل الميلاد. ونظراً لأهميتها التاريخية، فقد تم ترميم القلعة مرات عديدة على مر السنين للمحافظة عليها. يبلغ إرتفاع القلعة 45 متراً وتوفر للزوار إطلالة بانورامية جميلة على مدينة العلا.', 3, 9, NULL, 'https://www.google.com/maps/search/?api=1&query=26.625888,37.915277', 'https://pbs.twimg.com/media/Elz37GkXEAEGnjq.jpg', 'al_ula_fort.html', 'تراثية'),
(451, 'بلدة العلا القديمة', 'في مدينة العلا القديمة، يمكنك التجوّل بين أسواقها التراثيّة واكتشاف بضائعها التقليديّة والسفر عبر الزمن أثناء مرورك على آثارها وشواهدها التاريخيّة.', 3, 9, NULL, 'https://www.google.com/maps/search/?api=1&query=26.625071,37.915038', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ula-12-4/IMG_5511.jpg.thumb.1200.1200.jpg/', 'old_town_alula.html', 'تراثية'),
(452, 'حي الجديدة', 'صُممت الجديدة لتكون الوجهة الحيوية الأحدث في العلا، بالقرب من الواحة والبلدة القديمة، مع خيارات متعددة من المطاعم والمحلات التجارية بالإضافة إلى الفعاليات الترفيهية والفنية المتنوعة. تتميز الجديدة بموقعها المركزي وأجوائها المريحة المستوحاة من طبيعة وتراث العلا مع أجمل الأعمال الفنية التي تُزيّن جدرانها بأسلوب معاصر، وأكبر سجادة مصنوعة يدوياً في العالم لتُغطي طرقاتها المخصصة حصرياً للمشي', 3, 9, NULL, 'https://www.google.com/maps/place/26%C2%B037\'49.9%22N+37%C2%B054\'44.4%22E/@26.630527,37.912341,17z/data=!3m1!4b1!4m4!3m3!8m2!3d26.630527!4d37.912341?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ula-12-36/IMG_5543.jpg.thumb.1200.1200.jpg/', 'al_jadidah_neighborhood_alula.html', 'سياحية'),
(453, 'حرة عويرض', 'حرة عويرض هي حرة بركانية، تقع غرب محافظة العلا، وتعد أكبر محمية طبيعية في محافظة العلا، وتحتوي على 19 نوعًا من الحيوانات المهددة بالانقراض، وعلى 43 نوعًا من الطيور؛ منها 8 أنواع جارحة، كما أنها تحتوي على 55 نوعًا من النباتات النادرة.', 3, 9, NULL, 'https://www.google.com/maps/search/?api=1&query=26.632404,37.896615', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ula-12-9/IMG_5362.jpg.thumb.1200.1200.jpg/', 'hara_uwairid_alula.html', 'طبيعية'),
(454, 'مقبرة الأسود', 'مقبرة الأسود هي مجموعة من المقابر الأثرية منحوت على واجهتها رسم لأربعة أسود، تبعد مسافة 25 كم جنوب مدائن صالح. يعود تاريخها إلى القرن الخامس قبل الميلاد أي في الفترة الأولى من مملكة دادان التي كانت تحكم المنطقة تلك الفترة', 3, 9, NULL, 'https://www.google.com/maps/place/26%C2%B038\'50.4%22N+37%C2%B054\'57.8%22E/@26.64734,37.916066,17z/data=!3m1!4b1!4m4!3m3!8m2!3d26.64734!4d37.916066?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ula-12-8/IMG_5348.jpg.thumb.1200.1200.jpg/', 'black_graves_alula.html', 'تراثية'),
(455, 'دادان', 'من بين الاكتشافات الأكثر أهمية في العلا هي مدينة دادان، عاصمة مملكتي دادان ولحيان. وتعود المدينة التي بُنيتْ بدقة من الحجر وتتربّع على واحة الوادي إلى أواخر القرن التاسع وأوائل القرن الثامن قبل الميلاد (مملكة دادان) والقرن الخامس - الثاني قبل الميلاد (مملكة لحيان).', 3, 9, NULL, 'https://www.google.com/maps/search/?api=1&query=26.647129,37.916082', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ula-12-0/IMG_5331.jpg.thumb.1200.1200.jpg/', 'dadan_alula.html', 'تراثية'),
(456, 'زيبلاين العلا', 'تجربة زيبلاين العلا تعد إحدى أمتع التجارب للساعين وراء المغامرة والإثارة، فكل ما عليك فعله هو أن تحجز موعدًا وتهيئ نفسك لأجواء المغامرة. يعيش المغامر مع زيبلاين العلا أجواء مثيرة حيث ينطلق بسرعة 100 كلم في الساعة عبر مناظر جبال العلا الخلابة في تجربة لا تنسى على الإطلاق.', 3, 9, NULL, 'https://www.google.com/maps/place/26%C2%B040\'32.3%22N+37%C2%B052\'00.3%22E/@26.675645,37.866736,17z/data=!3m1!4b1!4m4!3m3!8m2!3d26.675645!4d37.866736?entry=ttu&g_ep=EgoyMDI1MTAyNy4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ula-12-11/DSC02801-2.jpg.thumb.1200.1200.jpg/', 'alula_zipline.html', 'ترفيهية'),
(457, 'جبل عكمة', 'جبل عكمة هو موقع أثري يقع في شمال غرب محافظة العلا في منطقة المدينة المنورة، يحتوي على الكتابات والنقوش الأثرية التي تعود إلى الحضارة اللحيانية في القرن السادس قبل الميلاد إلى القرن الثاني قبل الميلاد، وهو عبارة عن واد ضيق ينحدر من جبل عكمة وعلى بعد ثلاثة كيلومترات من مدينة العُلا.', 3, 9, NULL, 'https://www.google.com/maps/search/?api=1&query=26.687006,37.901546', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ula-12-1/IMG_5323.jpg.thumb.1200.1200.jpg/', 'jabal_ukmah_alula.html', 'طبيعية'),
(458, 'مسرح مرايا', 'مسرح مرايا أو مسرح المرايا هو مبنى مسرحي يقع في مدينة العلا السعودية ويتسع لنحو 500 شخص. يحتوي المبنى بالإضافة إلى المسرح على صالة عرض للفنون العالمية، ومناطق لاستقبال الضيوف والفنانين، وغرف لتجهيز الفرق الفنية قبل الصعود للمسرح، ومنطقة خاصة بكبار الشخصيات.', 3, 9, NULL, 'https://www.google.com/maps/search/?api=1&query=26.74335,37.865173', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ula-12-6/IMG_5460.jpg.thumb.1200.1200.jpg/', 'maraya_theater_alula.html', 'سياحية'),
(459, 'قوس مقرط الدبوس الجبلي', 'يعد جبل القوس، المعروف أيضاً باسم \"مقرط الدبوس\"، أحد أشهر التكوينات الصخرية في صحراء العلا، ويمتاز بهيكله على شكل الجسر، والذي يشبه قوس قزح محاط بسحابتين، وهو مشهد نادر بالفعل.', 3, 9, NULL, 'https://www.google.com/maps/search/?api=1&query=27.049366,37.766125', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-ula-12-2/IMG_5442.jpg.thumb.1200.1200.jpg/', 'maqrat_al_dabbous_arch_alula.html', 'طبيعية'),
(460, 'الغراميل', 'في صحراء العلا الشاسعة، يطل موقع الغراميل بسحر تضاريسه المنحوتة بإعجوبة، يفسر الجيولوجيون وجودها بأنها بقايا لجبل ضخم، تكفلت به الطبيعة وعوامل التعرية، مشكلة تكوينات صخرية متباينة.', 3, 9, NULL, 'https://www.google.com/maps/search/?api=1&query=27.3247898,37.7564283', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/alula/algharameel-alula/algharameel-in-alula.jpg.thumb.1200.1210.jpg', 'al_gharamil_alula.html', 'طبيعية'),
(461, 'حلبة كورنيش جدة | سباق الفورمولا 1', 'يُعد سباق جائزة السعودية الكبرى للفورمولا 1 جزءاً من الرؤية الأوسع لمستقبل المملكة، والتي تهدف إلى الارتقاء بمستوى جودة الحياة لجميع شرائح المجتمع بدءاً من النواحي الثقافية ووصولاً إلى الرياضة. لذلك تستضيف المملكة أكبر المهرجانات والسباقات العالمية، ومن أبرزها سباق الفورملا 1 المليء بالحماس والإثارة.\r\nالمسار السعودي، الذي يقع على طول ساحل البحر الأحمر، هو \"أسرع حلبة على الإطلاق في سباقات الفورمولا واحد\"، وفقًا للاتحاد الدولي للسيارات. كما أن تلك الحلبة تضم أكبر عدد من الزوايا التي تصل ل 27.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.625988166190805,39.12057520734766', 'https://www.visitsaudi.com/content/dam/wvs/destinations/jeddah/formula-1-stc-saudi-arabian-grand.jpeg', 'jeddah_corniche_f1_circuit.html', 'ترفيهية'),
(462, 'ردسي مول', 'حين نذكر اسم ردسي مول، تتمثل في الأذهان صورة العائلة والأصدقاء وسط أجواء مفعمة بالحياة والمرح ستقودك للاستمتاع بتجربة تسوق لا مثيل لها، حيث يعد من أكبر المراكز التجارية في الساحل الغربي للمملكة بمساحة طابقية مقدارها 242,000 متر مربع، ووجهة مثالية قريبة من الواجهة البحرية.', 1, 3, NULL, 'https://www.google.com/maps/search/?api=1&query=21.627750104045056,39.111077242320626', 'https://scth.scene7.com/is/image/scth/redsea-mall-new:crop-1920x1080?defaultImage=redsea-mall-new&wid=1920&hei=1080', 'redsea_mall_jeddah.html', 'ترفيهية'),
(463, 'مطار الملك عبدالعزيز الدولي', 'مطار الملك عبد العزيز الدولي، الأيقونة الجوية في مدينة جدة، يُعد بوابة المملكة العربية السعودية إلى الحرمين الشريفين. يقع على بُعد حوالي 19 كيلومترًا شمال مدينة جدة في منطقة مكة المكرّمة', 1, 3, NULL, 'https://maps.app.goo.gl/R7bEZQ45gC1KWyeQ7', 'https://saudipedia.com/saudipedia/uploads/images/2023/08/21/61587.jpg', 'king_abdulaziz_international_airport_jeddah.html', 'سياحية'),
(464, 'الحِجر (مدائن صالح)', 'أهم موقع أثري في العلا وموقع تراث عالمي لليونسكو. كانت مدينة نبطية قديمة.يضم مئات المقابر المنحوتة في الصخور الرملية، وأشهرها مقبرة لحيان بن قوزة، بالإضافة إلى محطة سكة حديد الحجاز.', 3, 9, 9, 'https://maps.app.goo.gl/pZe4LSu6WcGxtfxx7', 'https://cnn-arabic-images.cnn.io/cloudinary/image/upload/w_1920,c_scale,q_auto/cnnarabic/2023/05/11/images/239910.jpg', 'madain_saleh_alula.html', 'تراثية'),
(465, 'مناطيد الهواء الساخن', 'انطلق في رحلة آسرة مع تجربة منطاد الهواء الساخن في العلا، الذي يرفعك على ارتفاع 1200 متر فوق سطح الأرض لإطلالة بانورامية على المناظر الطبيعية الصحراوية المهيبة في المنطقة والمواقع الأثرية.', 3, 9, 9, 'https://maps.app.goo.gl/76wb4o6huz2qPEEw7', 'https://ar.timeoutjeddah.com/cloud/artimeoutjeddah/2023/10/03/air-baloon-alula-1024x818.jpg', 'alula_hot_air_balloons.md', 'ترفيهية'),
(466, 'صخرة الفيل', 'اقترب من صخرة الفيل المحبوبة في العلا، وهي معلم مميز ومكان لا يُفوّت. ترتفع هذه الأعجوبة الطبيعية، التي تشكلت بفعل ملايين السنين من عوامل التعرية، 52 مترًا.', 3, 9, 10, 'https://maps.app.goo.gl/QiuWf4VxZ1Lx1dLm6', 'https://welcomesaudi-media.s3.me-south-1.amazonaws.com/0000/1/2021/07/23/41-elephant-rock-al-ula-madinah-province.jpg', 'elephant_rock_alula.md', 'سياحية'),
(467, 'قلعة تبوك الإسلامية الأثرية', 'قلعة تاريخية يرجع تاريخها إلى عام 1559م، وكانت محطة رئيسية لحجاج وقوافل التجارة، وتضم متحفاً يعرض نقوشاً وصوراً ومقتنيات تراثية. تقع القلعة على الطريق القديم الذي يربط بلاد الشام بالمدينة المنورة.', 6, 60, 7, 'https://maps.app.goo.gl/iykEhcyvyTNryXg56', 'https://destinationksa.com/wp-content/uploads/content/%D9%82%D9%84%D8%B9%D8%A9-%D8%AA%D8%A8%D9%88%D9%83-5.jpg', 'Tabuk_Islamic_Archaeological_Castle.html', 'تراثية'),
(468, 'محطة سكة حديد الحجاز', 'إحدى المحطات الرئيسية لسكة الحديد، وهي ثالث أكبر المحطات مساحة بعد المدينة المنورة والعلا، وتم تحويلها إلى متحف يضم أدوات تشغيل المحطة. كانت تُستخدم لنقل الحجاج بين دمشق والمدينة المنورة.', 6, 60, 7, 'https://maps.app.goo.gl/HC6uFsN7tBgkhz4g7', 'https://scth.scene7.com/is/image/scth/tabuk-station-in-al-hijaz-railway-banner-1:crop-760x570?defaultImage=tabuk-station-in-al-hijaz-railway-banner-1', 'Hejaz_Railway_Station.html', 'ثقافية'),
(469, 'شاطئ قيال', 'يقع على بعد 190 كيلومترًا غرب مدينة تبوك. يتميز بصفاء مياهه، ويجذب عشاق الرياضات البحرية، ويوفر فرصاً للغوص والتخييم وصيد الأسماك، حيث تنتشر قبالته جزر متناثرة والشعب المرجانية الملونة.', 6, 66, 8, 'https://maps.app.goo.gl/nTNpUAiSHaDU6zGB8', 'https://explore.rehlat.ae/static/media/searchdestination/thingstodo/images/tabuk/qayal_beach/large_pexels-pok-rie-4024132.webp', 'Qayal_Beach.html', 'ترفيهية'),
(470, 'قلعة الزريب', 'إحدى قلاع طريق الحج المصري وتقع شرق محافظة الوجه، وقد أُنشئت في عام 1617م لتأمين الحجاج وخزن ودائعهم وأقواتهم.', 6, 58, 7, 'https://maps.app.goo.gl/ZaZ3ECZaARWETNcC9', 'https://www.spa.gov.sa/_next/image?url=https%3A%2F%2Fportalcdn.spa.gov.sa%2Fbackend%2Foriginal%2F202303%2Fh6OOv4Cf89bSlXP0uVrhR558ziTz7jREdej80v3v.jpg&w=3840&q=75', 'Al_Zareeb_Castle.html', 'تراثية'),
(471, 'صحراء حسمى', 'تتميز بجمالها الطبيعي وبتكوينات الحجر الرملي التي تشبه المنحدرات والقلاع.', 6, 62, 9, 'https://maps.app.goo.gl/fCkh7E1dHo4721ZK8', 'https://portalcdn.spa.gov.sa/backend/original/202201/4ca5a2c9b298a67794a87ae2f4e11754.jpeg', 'Hisma_Desert.html', 'طبيعية'),
(472, 'مدائن شعيب (مدين)', 'آثار قوم شعيب: موقع أثري مهم يضم واجهات صخرية ومنحوتات في الجبال تعود إلى الأنباط وأقوام ما قبل الإسلام.', 6, 57, 8, 'https://maps.app.goo.gl/FyFvppN1dRe4JV5b6', 'https://vid.alarabiya.net/images/2020/10/05/5b9cdbc6-28d7-4f1f-bda9-dbb913bd08c0/5b9cdbc6-28d7-4f1f-bda9-dbb913bd08c0_16x9_600x338.jpg', 'Midian_(Mada\'in_Shu\'aib).html', 'تراثية'),
(473, 'مسجد التوبة (مسجد الرسول)', 'يعتقد أنه بني في المكان الذي صلى فيه النبي محمد صلى الله عليه وسلم أثناء غزوة تبوك. يتميز بتصميمه المعماري القديم.', 6, 60, 7, 'https://maps.app.goo.gl/v4jwAgbvgie1osYw6', 'https://scth.scene7.com/is/image/scth/masjid-altawbah-in-tabuk-1:crop-760x570?defaultImage=masjid-altawbah-in-tabuk-1', 'Al_Tawbah_Mosque_(The_Prophet\'s_Mosque).html', 'دينية'),
(474, 'مطار خليج نيوم', 'يُعد نقطة الوصول الرئيسية للمستثمرين والعاملين والزوار الأوائل إلى مشروع نيوم.', 6, 68, 10, 'https://maps.app.goo.gl/kbjP17f5Z7TuNjWk7', 'https://static.srpcdigital.com/styles/1037xauto/public/2019/06/25/WhatsApp-Image-201529-06-25-at-7.52.jpg.webp', 'Neom_Bay_Airport.html', 'سياحية'),
(475, 'مطار الأمير سلطان بن عبد العزيز الدولي', 'يُعتبر نافذة جوية مهمة لدعم الحركة الاقتصادية، وله أهمية كبيرة لقربه من المشاريع الكبرى في الشمال الغربي مثل مشروع نيوم وأمالا ومشروع البحر الأحمر.', 6, 60, 8, 'https://maps.app.goo.gl/yDGqy8QFa9kSqwru5', 'https://media.assettype.com/ajel%2F2022-11%2Ff66f7473-b569-4d8b-9ec3-75d2055f8241%2FNew_Project___2022_11_02T230353_074.png?w=480&auto=format%2Ccompress&fit=max', 'Prince_Sultan_Bin_Abdulaziz_International_Airport.html', 'سياحية'),
(476, 'جزيرة شُورى', 'تُعد \"الجزيرة الرئيسية\" والمحور المركزي للمشروع، ستضم 11 فندقاً فاخراً، ومرسى لليخوت، وصممت على شكل مرجاني.', 6, 58, 9, 'https://maps.app.goo.gl/urHyLiNZBc4KuHQX6', 'https://saudipedia.com/saudipedia/uploads/images/2023/08/22/63421.jpg', 'Shura_Island.html', 'ترفيهية'),
(477, 'جزيرة شيبارة', 'منتجع فاخر يتميز بتصميمه المعماري المستقبلي المتمثل في كبسولات عائمة فوق الماء، يركز على الفخامة القصوى والاستدامة البيئية.', 6, 58, 9, 'https://maps.app.goo.gl/7YG3cWkAAFhb2wcPA', 'https://saudipedia.com/saudipedia/uploads/images/2025/05/10/196991.jpg', 'Sheybarah_Island.html', 'سياحية'),
(478, 'مطار البحر الأحمر الدولي', 'مطار دولي جديد صُمم لخدمة المشروع، ويعمل بالكامل على الطاقة المتجددة (الشمسية).', 6, 59, 10, 'https://maps.app.goo.gl/3iAnrRXo9prNPfGg8', 'https://www.visitredsea.com/content/dam/red-sea/en/master/before-you-visit/the-airport-and-flights/images/hero-airports-and-flights.jpg/_jcr_content/renditions/cq5dam.web.2880.0.webp?ts=1744121673601', 'Red_Sea_International_Airport.html', 'سياحية'),
(479, 'وادي الديسة', 'يتميز بتكوينات صخرية عملاقة تشبه الأعمدة، نحتتها عوامل التعرية. الوادي خصب وتجري فيه المياه (عادة بعد الأمطار)', 6, 60, 9, 'https://maps.app.goo.gl/hkK6CMEDKN4CzYCw8', 'https://cnn-arabic-images.cnn.io/cloudinary/image/upload/w_1920,c_scale,q_auto/cnnarabic/2020/07/16/images/159958.jpg', 'Al_Disah_Valley.html', 'طبيعية'),
(480, 'جبل اللوز', 'يبلغ ارتفاعه حوالي 2549 مترًا. يُعد الجبل الوحيد في السعودية الذي يشهد تساقطاً سنوياً للثلوج بغزارة في الشتاء،', 6, 60, 9, 'https://maps.app.goo.gl/WKytXJBHTwfiC9QZ9', 'https://cnn-arabic-images.cnn.io/cloudinary/image/upload/w_640,c_scale,q_auto/cnnarabic/2022/12/30/images/229562.jpg', 'Jabal_Al_Lawz.html', 'طبيعية'),
(481, 'جامع الوالدين', 'يتميز بتصميم فريد وحديث مستوحى من العمارة الإسلامية، ويحتوي على ست مآذن شاهقة (ارتفاع 46 متراً) وقبة رئيسية ضخمة (قطر 25 متراً) تُعد من أكبر القباب في المنطقة.', 6, 60, 10, 'https://maps.app.goo.gl/Fk9bub5x88g7XxaQ8', 'https://www.okaz.com.sa/uploads/images/2023/06/29/2128850.JPG', 'Al_Walidain_Grand_Mosque.html', 'دينية'),
(482, 'مطار عرعر المحلي', 'يُعد الرابط الجوي الرئيسي لمدينة عرعر، ويوفر الاتصال بشبكة المطارات الداخلية في المملكة.', 8, 65, 7, 'https://maps.app.goo.gl/vUB8i3LpexVDtLAf9', 'https://cdn.alweb.com/thumbs/travel/article/fit710x532/%D8%A3%D9%87%D9%85-%D8%A7%D9%84%D9%85%D8%B9%D9%84%D9%88%D9%85%D8%A7%D8%AA-%D8%B9%D9%86-%D9%85%D8%B7%D8%A7%D8%B1-%D8%B9%D8%B1%D8%B9%D8%B1-%D8%A7%D9%84%D9%85%D8%AD%D9%84%D9%8A.jpg', 'Arar_Domestic_Airport.html', 'سياحية'),
(483, 'قرية لينة التاريخية', 'تُعد من أقدم مواقع الاستيطان البشري في الجزيرة العربية ومن منازل درب زبيدة الشهير (طريق حجاج العراق).', 8, 67, 8, 'https://maps.app.goo.gl/7HDz9rrJ4YuxUswt5', 'https://blog.bayut.sa/uploads/2024/12/image-1440x900-52-1-1-1024x640.jpg', 'Laynah_Historical_Village.html', 'تراثية'),
(484, 'قصر الإمارة القديم (عرعر)', 'أحد أقدم القصور في المنطقة، ويتميز بالبناء من أحجار الجبال ليعكس العمارة المحلية القديمة.', 8, 65, 7, 'https://maps.app.goo.gl/eRWkMdRZgHEjoKbG9?g_st=ipc', 'https://saudipedia.com/saudipedia/uploads/images/2024/12/09/165786.jpg', 'Old_Emara_Palace_(Arar).html', 'تراثية'),
(485, 'متحف الحدود الشمالية (عرعر)', 'المتحف الرئيسي في المنطقة، يسلط الضوء على الأبعاد الثقافية والتاريخية لمنطقة الحدود الشمالية ويضم مجموعة واسعة من القطع الأثرية والمقتنيات التراثية.', 8, 65, 6, 'https://maps.app.goo.gl/DuL455n6wNVr5EgH8', 'https://annahar-news.com/uploads/news/annahar-news.com1688049363.jpeg', 'Northern_Borders_Museum_(Arar).html', 'سياحية'),
(486, 'محمية وادي معيلة', 'محمية طبيعية ووجهة برية شهيرة، تزخر بالنباتات البرية وأزهار الديدحان الحمراء في موسم الربيع.', 8, 61, 6, 'https://maps.app.goo.gl/7DcL25wRfzA93TFn6', 'https://prod-wafy-api.s3.eu-central-1.amazonaws.com/images/places/497528/7/20210623063240/497528-7-20210623063240-501234.jpg', 'Wadi_Mu\'aylah_Reserve.html', 'سياحية'),
(487, 'سد وادي بدنة', 'مناطق طبيعية ووديان خلابة، يقصدها الأهالي والزوار للاستمتاع بالطبيعة، خاصة في فصل الربيع حيث تتحول إلى بساط أخضر مناسب للتخييم والقنص.', 8, 65, 7, 'https://maps.app.goo.gl/hbzio9xFhxnPFyiU6', 'https://saudipedia.com/saudipedia/uploads/images/2024/12/02/165716.jpg', 'Wadi_Badnah_Dam.html', 'طبيعية'),
(488, 'جبل أقرن', 'وجهة مناسبة لمحبي المشي الجبلي والرحلات والمغامرات.', 8, 64, 8, 'https://maps.app.goo.gl/uDZE9jFFVczXtHhc8', 'https://blog.bayut.sa/uploads/2025/05/%D8%AC%D8%A8%D9%84_%D8%A3%D9%82%D8%B1%D9%86AR052625-2-1024x640.jpg', 'Jabal_Aqran.html', 'طبيعية'),
(489, 'مسجد الأمير عبد الله بن عبد العزيز', 'يُعتبر أحد أجمل المساجد في عرعر، ويتميز بتصميمه الخلاب وزخارفه ونقوشه الإسلامية، ويقع وسط حدائق مزينة، مما يجعله معلماً حضارياً في المدينة.', 8, 65, 8, 'https://maps.app.goo.gl/MJpEqpP3k4yfoZrUA', 'https://al-rahhala.com/wp-content/uploads/2022/04/%D9%85%D8%B3%D8%AC%D8%AF-%D8%A7%D9%84%D8%A3%D9%85%D9%8A%D8%B1-%D8%B9%D8%A8%D8%AF-%D8%A7%D9%84%D9%84%D9%87-%D8%A8%D9%86-%D8%B9%D8%A8%D8%AF-%D8%A7%D9%84%D8%B9%D8%B2%D9%8A%D8%B2.jpg', 'Prince_Abdullah_Bin_Abdulaziz_Mosque.html', 'دينية'),
(490, 'جبل السودة', 'يُعد جبل السودة أعلى قمة في المملكة العربية السعودية بارتفاع يقارب 3,015 متر فوق سطح البحر، ويقع شمال غرب مدينة أبها في منطقة عسير. يتميز الجبل بمناخه البارد طوال العام وغاباته الكثيفة من شجر العرعر، إضافةً إلى السحب والضباب التي تغطيه أغلب أيام السنة، مما يجعله من أجمل الوجهات السياحية في المملكة', 13, 47, 10, 'https://www.google.com/maps/place/Jabal+Al+Souda/@18.246132,42.357204,12z', 'https://saudipedia.com/saudipedia/uploads/images/2023/06/21/59375.jpg', 'Jabal_Al_soudah.html', 'طبيعية'),
(491, 'متحف الراقدي', 'يعرض تحفاً ومقتنيات من الفن والتاريخ المحلي، جزء من المشغولات اليدوية والتراث الثقافي.', 13, 47, 6, 'https://maps.app.goo.gl/ZW8fZZYZERuU9s9P7?g_st=iw', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQUjN_-gxVCHAXfzKNpwFGzpkqvEHiwfL_iBA&s', 'Al_Raqadi_Museum.html', 'تراثية'),
(492, 'الجبل الأخضر', 'الجبل الأخضر في قلب أبها اسمه الرسمي جبل ذَرّة. تميّزه الحقيقي ليلًا: مصاطب مُضاءة باللون الأخضر تُرى من مسافة طويلة، ومعه تلفريك يصعد بك من منطقة “أبها الجديدة/محطة التلفريك” إلى القمة بإطلالات بانورامية على المدينة وبحيرة السد. المكان كان مقرًا عسكريًا/لاسلكيًا قبل تطويره كواجهة سياحية فيها مطاعم ومقاهٍ.', 13, 47, 9, 'https://maps.app.goo.gl/h3fdLTDTWxK8wrCT9?g_st=iw', 'https://www.urtrips.com/wp-content/uploads/2022/10/The-green-mountain-Abha1.jpg', 'The_Green_Mountain.html', 'طبيعية'),
(493, 'قرية الحبلة', 'قرية الحبلة إحدى عجائب منطقة عسير، تقع على بُعد حوالي 52 كيلومترًا جنوب غرب مدينة أبها في مركز الواديين. تتميز بموقعها الفريد على حافة منحدر جبلي شاهق، مما يجعلها تبدو وكأنها معلقة بين السماء والأرض. وتشتهر بتاريخها وتراثها الغني، مع وجود منتزه الحبلة والتلفريك والمتحف التراثي', 13, 47, 5, 'https://www.google.com/maps/place/18%C2%B002\'17%22N+42%C2%B052\'22%22E/@18.0380806,42.8726611,15z', 'https://cnn-arabic-images.cnn.io/cloudinary/image/upload/w_1920,c_scale,q_auto/cnnarabic/2020/12/20/images/172825.avif', 'Al_Habala_Village.html', 'سياحية'),
(494, 'قصر شدا', 'قصر شدا هو معلم تاريخي في أبها بمنطقة عسير، يتميز بتصميمه المعماري الفريد ويعود تاريخه إلى عام 1927م.', 13, 47, 6, 'https://maps.app.goo.gl/X1tGHdCTULMhYQga8?g_st=iw', 'https://static.hiamag.com/styles/autox754/public/article/19/06/2021/10655401-350936909.jpg?itok=PZDQxMEq', 'Shada_Palace.html', 'تراثية'),
(495, 'بحيرة السد', 'بحيرة تقع في مدينة أبها على ارتفاع حوالي 2,200 متر فوق سطح البحر، وتُعد واحدة من أبرز المعالم السياحية في المنطقة. تشكلت البحيرة بعد بناء سد أبها للاحتفاظ بمياه الأمطار والسيول، وهي محاطة بمساحات خضراء ومناظر طبيعية خلابة.توفر بحيرة سد أبها مكانًا مثاليًا للاسترخاء، حيث يمكن للزوار الاستمتاع بجمال المياه الهادئة وممارسة أنشطة متنوعة مثل المشي والتنزه على ضفافها. وتُضاء المنطقة المحيطة بالبحيرة في الليل، مما يخلق أجواء رائعة تجذب العائلات والسياح للاستمتاع بجوها اللطيف، خصوصًا خلال الصيف.', 13, 47, 8, 'https://maps.app.goo.gl/77nRvAd6HKxEayQm7?g_st=iw', 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/1a/8b/b2/b5/a-different-view.jpg?w=900&h=-1&s=1', 'Dam_Lake.html', 'طبيعية'),
(496, 'منتزه أبو خيال', 'منتزه طبيعي في أبها يطل على المدينة ويضم مساحات خضراء ومرافق ترفيهية', 13, 47, 3, 'https://maps.app.goo.gl/W9PVLtEWrpPXHHkNA?g_st=ipc', 'https://murshdk.com/Place/104f72c0dfbb48a8935d87bb96e664cc.jpg', 'Abu_Khayal_Park.html', 'طبيعية'),
(497, 'قرية المفتاحة', 'قرية ثقافية وتراثية في مدينة أبها، مصممة على الطراز المعماري العسيري التقليدي، تضم معارض فنية، مركز الملك فهد الثقافي', 13, 47, 2, 'https://maps.app.goo.gl/ya9jYhq4PZzWSXh2A?g_st=ipc', 'https://saudipedia.com/saudipedia/uploads/images/2024/07/28/95146.jpg', 'Al_Muftaha_Village.html', 'تراثية'),
(498, 'ممشى الضباب', 'ممر جبلي يتيح للزائر المشي وسط الضباب والمشاهد الجبلية الضبابية، تجربة فريدة.', 13, 47, 10, 'https://maps.app.goo.gl/QsTuM4a8bqNPkqx17?g_st=iw', 'https://www.alyaum.com/uploads/images/2023/11/06/2170458.jpg', 'The_Fog_Walkway.html', 'طبيعية'),
(499, 'وادي حلي', 'وادي طبيعي في محايل عسير يتميز بالغابات الكثيفة والمزارع الخضراء، ويُعد من أبرز الأماكن للرحلات العائلية والمشي بين الطبيعة', 13, 51, 8, 'https://www.google.com/maps/place/Wadi+Hali,+Mahayel+Asir', 'https://saudipedia.com/saudipedia/uploads/images/2023/09/26/67632.jpg', 'Wadi_Hali.html', 'طبيعية'),
(500, 'جبل السرو', 'جبل مرتفع في محايل عسير يتميز بالمناظر البانورامية الخلابة والهواء النقي، ويعتبر وجهة رائعة للتصوير والتخييم والاستمتاع بالطبيعة الجبلية الخضراء', 13, 51, 9, 'https://maps.app.goo.gl/Y9wBr1eJBHznr8X7A?g_st=ipc', 'https://www.qpedia.org//public/topics/820974_3589202209.jpg', 'Jabal_Serou.html', 'طبيعية'),
(501, 'منتزه جبل المقيصرة', 'مطلّ طبيعي في قلب محايل يقسمها شرق/غرب ويوفّر إطلالة بانورامية على المدينة، وموقع شائع للطيران الشراعي.', 13, 51, 8, 'https://maps.app.goo.gl/pxnKXa6tCNrsbs6T7?g_st=iw', 'https://aslelmkan.com/wp-content/uploads/2023/08/%D9%85%D9%86%D8%AA%D8%B2%D9%87%D8%A7%D8%AA-%D9%85%D8%AD%D8%A7%D9%8A%D9%84-%D8%B9%D8%B3%D9%8A%D8%B1.jpg', 'Jabal_Al_Moqaisirah_Park.html', 'طبيعية'),
(502, 'السوق الشعبي بمحايل', 'سوق تراثي قديم كان يُقام يوم السبت فقط وأصبح يوميًا؛ منتجات محلية (عسل، سمن، فخار، ملابس تراثية) وأجواء شعبية.', 13, 51, 7, 'https://maps.app.goo.gl/4aNZ9V2a4LeNtvfC8?g_st=iw', 'https://discoveraseer.com/img/asset/YXNzZXRzL2F0dHJhY3Rpb24vc29rLW1oYXlsLS53ZWJw/sok-mhayl-.webp?p=seo_pro_og&s=00c4c92fc355e804d87df0048d8ade84', 'Mahayel_Traditional_Market.html', 'تراثية'),
(503, 'منتزه عسير الوطني', 'أول منتزه وطني في المملكة، يمتد من قمم جبال عسير حتى سواحل البحر الأحمر، ويضم تنوعاً بيئياً فريداً.', 13, 47, 10, 'null', 'https://www.thaqfny.com/wp-content/uploads/2022/11/%D9%85%D9%86%D8%AA%D8%B2%D9%87%D8%A7%D8%AA-%D8%A7%D8%A8%D9%87%D8%A7-jpg.webp', 'Asir_National_Park.html', 'طبيعية'),
(504, 'محطة العربات المعلقة', 'تأخذك العربة في نزهة قصيرة من سماء مدينة أبها تمر بها على معالم متعددة من المدينة وسط أجواء ضبابية متفردة، يعد التلفريك الجبلي عامل جذب رئيسي في أبها ، مما يسمح للزوار بالوقوف والصعود في الهواء الطلق إذا رغبوا في ذلك. إذ يضمن رؤية بانورامية على كامل المنطقة.', 13, 47, 8, 'https://maps.app.goo.gl/knhKK2vZbyGAFHRz6', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/mountain-cable-car/AQ_2_W6954_f2723d27e0.jpg.thumb.1200.1200.jpg/', 'Cable_Car_Station.html', 'ترفيهية'),
(505, 'قرية رجال ألمع التراثية', 'قرية تاريخية مبنية من الحجر، تتميز بفنها المعماري الفريد \"القط العسيري\"، ومدرجة ضمن أفضل القرى السياحية عالمياً. يأخذك هذا المكان في رحلة إلى الماضي البعيد حيث المباني التراثية التي تشهد تفاصيل مرتبطة بثقافة الإنسان والمكان والمعروضات النادرة العائدة إلى مئات السنين', 13, 50, 10, 'https://maps.app.goo.gl/eAHkFRFqvS81yBR16', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/rijalalmaa-village/02.jpg.thumb.1210.1200.jpg', 'Rijal_Almaa_Village.html', 'تراثية'),
(506, 'شارع الفن', 'يضيف شارع الفن  بعداً مختلفاً لفعاليات موسم صيف عسير، من خلال تسليط الضوء على ثقافة عسير وتاريخها العريق، حيث تخلق الأنشطة رحلة فريدة، بين تجارب المأكولات والمشروبات، ومنطقة الأطفال، والعروض الحية المتنوعة، والحرف اليدوية المميزة.', 13, 47, 7, 'https://maps.app.goo.gl/xQkuJVgxvsuobJw38', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/the-art-street-and-al-muftaha-village/01.jpg.thumb.1210.1200.jpg', 'Art_Street.html', 'ترفيهية'),
(507, 'سوق الثلاثاء الشعبي', 'من أقدم الأسواق الشعبية في عسير ينتصف مدينة أبها مقدما عددا كبيرا من معارض البيع للمنتجات القديمة والحديثة ومنتجات المزارع المحلية وهو مزار سياحي لعشاق التراث والأصالة', 13, 47, 7, 'https://maps.app.goo.gl/WncocsuKKhNbE72d8', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/souq-al-thulatha-tuesday-market/STAI0047_401.jpg.thumb.1200.1210.jpg', 'Tuesday_Traditional_Market.html', 'تراثية'),
(508, 'قلعة شمسان', 'تقع قلعة شمسان إلى الشمال الشرقي من مدينة أبها ، وقد شيدت على قمة جبل شمسان الذي يبلغ ارتفاعه حوالي( 2300م) فوق سطح البحر، وهي تسيطر على الطريق القادم من عقبة شعار شمالاً ، حيث يعد الطريق الرئيس الذي يربط مدينة أبها بالمناطق الشمالية . ومن هنا فإن تشييد القلعة في هذا الموقع له أهميته العسكرية في إغلاق الطريق أمام أي هجوم يداهم المدينة من الناحية الشمالية. ولهذا فقد تنبه الوالي التركي محيي الدين باشا إلى أهمية تحصين شمال مدينة أبها فأصدر أوامره ببناء هذه القلعة الحصينة على رأس جبل شمسان ، واستمرت تؤدي مهمتها العسكرية حتى غادر الأتراك بلاد عسير عام ( 1337هـ /1918م) ثم هجرت فأصابها الخراب وتهدمت بعض أجزائها ، وفي عام (1352هـ /1933م) احتاجت القوات السعودية إلى استخدامها فوضعت فيها حامية عسكرية بعد أن قامت بترميم الأجزاء التي هدمت منها من قبل ذلك ، وإضافة بعض الوحدات البنائية اللازمة بها . وبقيت مستخدمة لمدة قصيرة تراوحت بين ست وسبع سنوات، ثم هجرت حتى وقتنا الحالي', 13, 47, 7, 'https://maps.app.goo.gl/hwRtwLaWHdky7nFq7', 'https://murshdk.com/Place/3768d910ab0d4a7999aaef5700d5cc9c.jpeg', 'Shamsan_Castle.html', 'تراثية'),
(509, 'حديقة السلام', 'تتزين حديقة السلام في أبها بالمناظر الطبيعية الخلابة التي تبرز جمالها النابض بالحياة وتحتوي على نافورة راقصة، والتي تمنح الزائرين تجربة فريدة من نوعها ليغمروا أنفسهم في مياه وموسيقى وعروض ضوئية آسرة. تحتوي الحديقة على عدد كبير من الأنشطة الترفيهية لضمان حصول الأطفال على متعة غير معهودة من خلال المراجيح وركوب الخيل.', 13, 47, 7, 'https://maps.app.goo.gl/wWwdhk6nbmFpSadJ7', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/al-salam-park/DSC_00457_8984506f4c.jpg.thumb.1200.1200.jpg/', 'Peace_Park.html', 'طبيعية'),
(510, 'بسطة القابل', 'أحد أقدم الاحياء في مدينة أبها وهو يقع على ضفاف الوادي يربطه جسر عثماني قديم بحديقة العدل علي الضفة الأخرى من الوادي، يحتفظ الحي بطرازه الترا ثي من جميع النواحي (التخطيط والممرات وأسلوب العمارة)، ويتضمن مباني تراثية مجمعة يتوسطها ساحة عامة تجتمع فيه الناس صباحا ومساء ويعرضون ما لديهم للبيع حتى سمي الحي بذلك الاسم', 13, 47, 5, 'https://maps.app.goo.gl/J83kfdv3aSvdjYmHA', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/al-basta-district/al-basta-district.jpg.thumb.1200.1200.jpg/', 'Basta_Al_Qabel.html', 'تراثية'),
(511, 'وادي ثراد', 'من أشهر وديان منطقة الباحة في محافظة العقيق بإضافة الى موقعه الجذاب', 12, 37, 4, 'https://www.google.com/maps/place/19%C2%B043\'19.2%22N+41%C2%B028\'55.5%22E/@19.7220015,41.4820864,17z/data=!3m1!4b1!4m4!3m3!8m2!3d19.7220015!4d41.4820864?entry=ttu&g_ep=EgoyMDI1MTAwOC4wIKXMDSoASAFQAw%3D%3D', 'https://lh3.googleusercontent.com/places/ANXAkqF8uu_Enp-ZUY6WhkAa1wjo1FjKnIbfgqU9CmYADzhrXXtK2GFXNbgw5WUIUZ5LPvP-YqwmVVeOHP3W0Bxes_93I7x_iokHYh8=s4800-w4000-h2252', 'Wadi_Tharad.html', 'طبيعية'),
(512, 'منتجع الكهوف', 'ﻳُﻌﺘﺒــﺮ ﻣﻨﺘﺠــﻊ اﻟﻜﻬــﻮف ﺑﺠﺒــﻞ ﺷــﺪا أﺣــﺪ اﺑــﺮز اﻟﻤﻮاﻗــﻊ اﻟﻄﺒﻴﻌﻴــﺔ اﻟﺘــﻲ ﻛﺎﻧــﺖ ﻣﺴــﻜﻨﺎً ﻟﻺﻧﺴــﺎن ﻗــﺪﻳﻤﺎً ﻣﻨــﺬ زﻣــﻦ ﺑﻌﻴــﺪ، ﺛــﻢ أﺻــﺒﺢ ﻣﻨﺘﺠــﻊ اﻟﻜﻬــﻮف ﻣــﺰاراً ﺳﻴﺎﺣﻴﺎ ً', 12, 39, 5, 'https://www.google.com/maps/search/?api=1&query=19.7348959204245,41.3813523865077', 'https://lh3.googleusercontent.com/places/ANXAkqGLi7Rglh1zX71eGheTQ3oQ4WwqzNEdnTmM2JQ-A6dgUBslyIzTuxCdjycimswbiB6pAME2g4OLv3Ll5wzrzHMe6fndYKjYi68=s4800-w3968-h2976', 'The_Caves_Resort.html', 'طبيعية'),
(513, 'جبل شدا الأسفل', 'يشكل جبل شدا الأسفل أحد قمم جبال منطقة الباحة الشاهقة وعليه تقع قرى شدا الأسفل، ويقصده السياح للاستمتاع بأجواء وتضاريس الطبيعة البديعة، ويمتاز جبل شدا الأسفل عما حوله من الجبال بكثرة الصخور الجرانيتية البالغة الضخامة، خاصة الكتل الصخرية بطولها الذي يقدر بمائتين متر تقريبا، ووجود عدد ضخم جداً من الهضاب والمغارات المظلمة والقابعة تحت الصخور العظيمة.', 12, 39, 6, 'https://www.google.com/maps/search/?api=1&query=19.732775,41.35967', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-bah-3-6/SA705702.jpg.thumb.1200.1200.jpg', 'Jabal_Shada.html', 'طبيعية'),
(514, 'محمية جبل شدا الأعلى', 'تعتبر محمية جبل شدا الأعلى واحدة من أكبر المحميات الطبيعية في السعودية، بمساحة 68.62 كيلومتر مربع في منطقة الباحة، وهي من أكثر الأماكن جذباً للسياح الراغبين بقضاء وقت استثنائي في قلب الطبيعة، ففيها يمكن مشاهدة الحيوانات والاستمتاع بشذى النباتات العطرية وتسلق الجبال واستكشاف المعالم الأثرية، والكهوف المحفورة برسومات ونقوش تعود إلى أكثر من 3000 عام، وركوب الدراجات ما بين الجبال الجرانيتية ذات الطبيعة الخلابة.', 12, 39, 9, 'https://www.google.com/maps/search/?api=1&query=19.847631,41.31622', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-bah-3-7/SA705664.jpg.thumb.1200.1200.jpg/', 'Jabal_Shada_Reserve.html', 'طبيعية'),
(515, 'الخلف والخليف', 'الخلف والخليف موقعان لقريتين متجاورتين تفصل بينهما مسافة كيلومترين فقط، عثر فيهما على بقايا أحياء سكنية مختلفة وبقايا مسجد الخلف المربع الشكل الذي تبلغ مساحته 324 متر مربع، ويظهر فيه الطابع التحصيني من حيث ارتفاع جدرانه وسماكتها ومتانة البناء، أما مسجد الخليف فقد أندثر ولم يبق منه شيء، ويحتوي الموقعان على العديد من المقابر، وكذلك النقوش الخطية التي عثر منها على 27 نقشاً تغطي فترة زمنية متقدمة من القرن الهجري الأول، وجميعها منقوشة بالخط الكوفي المتدرج من البسيط إلى المورق ثم المزهر، وتقع القريتان في محافظة قلوة التابعة لمنطقة الباحة في تهامة جنوب غرب السعودية.', 12, 45, 2, 'https://www.google.com/maps/place/19%C2%B058\'08.2%22N+41%C2%B013\'34.9%22E/@19.968942,41.226366,17z/data=!3m1!4b1!4m4!3m3!8m2!3d19.968942!4d41.226366?entry=ttu&g_ep=EgoyMDI1MTAwOC4wIKXMDSoASAFQAw%3D%3D', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-bah-3-2/SA705784.jpg.thumb.1200.1200.jpg', 'Al_Khalaf_and_Al_Khulaif.html', 'تراثية'),
(516, 'قرية ذي عين الاثرية', 'ﺳﻤﻴﺖ اﻟﻘﺮﻳﺔ ﺑـ\"ذي ﻋﻴﻦ\" ﻧﺴﺒﺔ ﻟﻌﻴﻦ اﻟﻤﺎء اﻟﺘﻲ ﺗﻨﺴﺎب ﻣﻦ اﻟﺠﺒـﺎل اﻟﻤﺠـﺎورة ﺑـﺪون اﻧﻘﻄﺎع ، وﺗﺘﻤﻴﺰ اﻟﻘﺮﻳﺔ ﺑﺸﻜﻠﻬﺎ اﻟﺠﻤﻴﻞ ، وﺗﻌﺘﺒﺮ ﻣﻦ اﻷﻣﺎﻛﻦ اﻟﺴﻴﺎﺣﻴﺔ اﻟﺮاﺋﻌﺔ اﻟﺘـﻲ ﺗﺠﺬب اﻟﻌﺪﻳﺪ ﻣﻦ اﻟﺰاﺋﺮﻳﻦ ﻟﺰﻳﺎرﺗﻬﺎ، واﻻﺳﺘﻤﺘﺎع ﺑﻤﻨﻈﺮ اﻟﻘﺮﻳﺔ اﻟﺮاﺋﻊ. قرية ذي عين الأثرية تقع في المخواة بمنطقة الباحة وتمتاز بتراث أصيل وتاريخ عريق وجبال آسرة، ويعود تاريخها إلى نهاية القرن العاشر الهجري، وعمرها نحو 400 عام، وفيها قصور عددها 58 ، وتتميز بموقعها الجغرافي ، الذي يجذب الزوار للاستمتاع بمرتفعاتها ومزارعها ومياهها العذبة.', 12, 46, 10, 'https://www.google.com/maps/search/?api=1&query=19.9313618982209,41.4424947865077', 'https://lh3.googleusercontent.com/places/ANXAkqFQ-PZ_WmE4ybiB6549XFlaPycuVrhc3k8IW7WXJTLF5ifF4_B5z0f49IkdD-IMITZSAV0DAw1laZaJL0WfNmagZrmATE0xXwE=s4800-w902-h600', 'Thee_Ain_Ancient_Village.html', 'تراثية'),
(517, 'مزرعة الجسر النباتية', 'ﺗﻘﻊ ﻣﺰرﻋﺔ اﻟﺠﺴﺮ ﻓﻲ ﻗﺮﻳﺔ اﻟﻘﺮﻳـﻊ، ﻣﺤﺎﻓﻈـﺔ ﺑﻠﺠﺮﺷـﻲ، وﺗﺤﺘﻀـﻦ اﻟﻌﺪﻳـــﺪ ﻣـــﻦ اﻷﻧﺸـــﻄﺔ ﻟﻤـــﺎ ﺗﺘﻤﻴـــﺰ ﺑـــﻪ ﻣـــﻦ ﻣﻨـــﺎﻇﺮ ﻃﺒﻴﻌﻴـــﺔ ﺧﻼﺑـــﺔ، ﺗﺤﺘــــــﻮي ﻋﻠــــــﻰ ﻗﺮاﺑــــــﺔ ٣٠٠ ﺷــــــﺠﺮة، ﺗﻀــــــﻢ اﻟﻌﺪﻳــــــﺪ ﻣــــــﻦ اﻷزﻫــــــﺎر واﻟﺸــــﺠﻴﺮات واﻟــــﻮرود، وﻳﻄﻠــــﻖ ﻋﻠﻴﻬــــﺎ ﺟﺴــــﺮ اﻟﺴــــﺤﺎب اﻟﻤﻌﻠــــﻖ وﻣﻤﺸــﻰ اﻟﻀــﺒﺎب ﻧﺴــﺒﺔ إﻟــﻰ اﻟﺠﺴــﺮ اﻟﺨﺸــﺒﻲ اﻟﻤﻌﻠــﻖ ﺑﻤﻨﺘﺼــﻒ اﻟﻤﺰرﻋﺔ، ﺑﻄﻮل ١٠٠م وﻳﺸﻜﻞ إﻃﻼﻟﺔ ﺳﺎﺣﺮة ﻋﻠﻰ ارﺟﺎء اﻟﻤﺰرﻋﺔ.', 12, 38, 1, 'https://www.google.com/maps/search/?api=1&query=19.84190689,41.57106193', 'https://bahaadmin.baha.gov.sa/Uploads/Images/eb62b7a0-f833-4822-bad9-63314b76c620_%D8%BA%D8%BA%D8%BA%D8%BA.png', 'Al_Jisr_Botanical_Farm.html', 'طبيعية'),
(518, 'مزرعة الوالدين', 'ﻧــﺬر ﻣﺎﻟﻜﻬــﺎ ﻣــﺎ ﻓــﻲ اﻟﻤﺰرﻋــﺔ ﻣــﻦ ﻣﻨﺘــﻮج زراﻋــﻲ ﺻــﺪﻗﺔ ﻋــﻦ واﻟﺪﻳﻪ ﻟﺬﻟﻚ ﺳﻤﻴﺖ ﺑﻤﺰرﻋﺔ اﻟﻮاﻟﺪﻳﻦ ﺗﺠﺴــﺪ ﺟﻬــﻮد أﺑﻨــﺎء اﻟﻤﻨﻄﻘــﺔ اﻟﻮاﻋــﺪة ﻟﻠﻨﻬــﻮض ﺑﻤﻨﻄﻘــﺔ اﻟﺒﺎﺣﺔ ﻟﺘﻜﻮن وﺟﻬﺔ ﺳﻴﺎﺣﻴﺔ ﻣﻤﻴﺰة.', 12, 41, 1, 'https://www.google.com/maps/place/Parents%E2%80%99+Farm/@19.8584598,41.5459864,17z/data=!3m1!4b1!4m6!3m5!1s0x15ef5d3560301489:0xe68f06744233e067!8m2!3d19.8584548!4d41.5485613!16s%2Fg%2F11nxrpm_bt?entry=ttu&g_ep=EgoyMDI1MTAwOC4wIKXMDSoASAFQAw%3D%3D', 'https://bahaadmin.baha.gov.sa/Uploads/Images/45e15a96-a81a-4313-80df-c8a138c5f346_%D9%84%D8%A7%D9%84%D8%A7.png', 'Tarhab_Farm.html', 'طبيعية'),
(519, 'بلجرشي القديمة', 'تمتاز محافظة بلجرشي بكونها مدينةً سياحية من الطراز الأول من حيث الجمال والطبيعية والأجواء الساحرة، ووجهةً للسياح للاستمتاع بأجوائها البديعة، وتقع بلجرشي جنوب مدينة الباحة بحوالي 25 كم، وتعد أكبر محافظات منطقة الباحة، وهي المحافظة الأولى في المنطقة والمركز التجاري والاقتصادي، وسميت بلجرشي بهذا الإسم نسبة إلى قبيلة بلجرشي التي تسكن في هذه المحافظة و هي احدى قبائل غامد، وتتميز بأنها جبلية ومناخها معتدل ويعد جبل حزنة بالمحافظة أعلى نقطه بمنطقة الباحة.', 12, 41, 4, 'https://www.google.com/maps/search/?api=1&query=19.859815,41.559282', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-bah-3-4/SA709416.jpg.thumb.1200.1200.jpg/', 'Old_Baljurashi.html', 'تراثية'),
(520, 'منتزه الدانة', 'ﺗُﺘـــﻴﺢ ﻟﻠﺰاﺋـــﺮﻳﻦ ﻓﺮﺻـــﺔ ﻗﻀـــﺎء وﻗـــﺖ ﻣُﻤﻴّـــﺰ ﻋﺒـــﺮ اﻟﺘﺠـــﻮّل وﺳـــﻂ اﻟﻄﺒﻴﻌـــﺔ اﻟﻐﻨﻴـــﺔ ﺑﻜـــﻞ ﻣﻌـــﺎﻧﻲ اﻟﺠﻤـــﺎل ، ﻓﻬـــﻲ ﺗﺤـــﻮي ﻣﺴـــﺎﺣﺎت ﺧﻀـــﺮاء وأﺷـــﺠﺎر ﻇﻠﻴﻠﺔ وﻛﺬﻟﻚ ﻣﻘﺎﻋﺪ ﻣﺨﺼﺼﺔ ﻟﺮاﺣﺔ اﻟﺰاﺋﺮﻳﻦ', 12, 41, 8, 'https://www.google.com/maps/search/?api=1&query=19.884226971919,41.5695126865077', 'https://bahaadmin.baha.gov.sa/Uploads/Images/94d4dc7b-8b60-49cd-b530-f06621891a91_image59.jpg', 'Al_Danah_Park.html', 'طبيعية'),
(521, 'مزرعة و أكواخ بساتين اللوز', 'ﺗﺠﺴـﺪ ﻣﺰرﻋــﺔ وأﻛــﻮاخ \"ﺑﺴــﺎﺗﻴﻦ اﻟﻠــﻮز\" ﺟﻬــﻮد أﺑﻨــﺎء اﻟﻤﻨﻄﻘــﺔ اﻟﻮاﻋــﺪة ﻟﻠﻨﻬــﻮض ﺑﻤﻨﻄﻘﺔ اﻟﺒﺎﺣﺔ ﻟﺘﻜﻮن وﺟﻬﺔ ﺳﻴﺎﺣﻴﺔ ﺗﻌﻜﺲ روﻋﺔ ﺑﻴﺌﺘﻨـﺎ اﻟﺮﻳﻔﻴـﺔ اﻟﺒﻜـﺮ وﻳﻤﻜـﻦ اﻟﺘﺠﻮل ﻓﻲ اﻛﻮاخ ﺧﺸﺒﻴﺔ ﻟﻠﻤﻨﺘﺠﻊ ﺣﻴﺚ ﻳﺤﺘﻮي ﻛﻞ ﻣﻨﺘﺠـﻊ ﻋﻠـﻰ ﻏـﺮﻓﺘﻴﻦ ﺑﻜﺎﻣـﻞ ﻣﻠﺤﻘﺎﺗﻬﺎ وﻣﻘﻬﻰ ﺻﻐﻴﺮ ﻣﺘﻜﺎﻣﻞ اﻟﺘﺠﻬﻴﺰات.', 12, 35, 2, 'https://www.google.com/maps/search/?api=1&query=19.99456211,41.51851466', 'https://scth.scene7.com/is/image/scth/almond-orchards-farm:crop-760x570?defaultImage=almond-orchards-farm', 'Bustan_Al_Luz_Farm_and_Huts.html', 'طبيعية'),
(522, 'قلعة الملد التراثية', 'تعد قلعة الملد من الأماكن السياحية المميزة في مدينة الباحة التي يرتادها الزوار والسياح، وتم بناء القلعة قديماً من قبل شقيقين لأغراض المراقبة العسكرية من أجل حماية القرية، كما جعلوها أيضاً موقعاً لتخزين الطعام، وعند زيارتها ستلاحظ وجود مبنى صغير يستخدمه زعماء القرى لإبرام اتفاقيات المصالحة بين القبائل وإجراء الصفقات التجارية نظراً لموقعه المهم على الطريق التجاري المؤدي إلى عسير.', 12, 33, 5, 'https://www.google.com/maps/search/?api=1&query=20.011975,41.498543', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-bah-3-9/SA709807.jpg.thumb.1200.1200.jpg', 'Al_Mulayd_Heritage_Fort.html', 'تراثية'),
(523, 'متنزه الأمير حسام', 'يشكل متنزه الأمير حسام متنفسًا لسكان مدينة الباحة وزوارها، حيث يعد من أجمل المتنزهات في المدينة، ويضم نافورة ضخمة تتزين بالعديد من الألوان في المساء وتنتشر حولها الكراسي والمقاعد المناسبة للجلوس وتبادل أطراف الحديث مع أحد الأصدقاء، بالإضافة لوجود ألعاب خاصة بالأطفال على شكل قصور ومبانٍ من عالم الخيال.', 12, 33, 9, 'https://www.google.com/maps/search/?api=1&query=20.011542,41.450223', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-bah-3-17/SA707450.jpg.thumb.1200.1200.jpg/', 'Prince_Hussam_Park.html', 'طبيعية'),
(524, 'ﺣﺪﻳﻘﺔ اللافندر', 'ﺗﻤﺜـــﻞ ﺣﺪﻳﻘـــﺔ اﻟﻼﻓﻨـــﺪر أﺣـــﺪ أﻫـــﻢ اﻟﻤﻮاﻗـــﻊ اﻟﺴـــﻴﺎﺣﻴﺔ اﻟﺒـــﺎرزة ﻓـــﻲ ﻣﻨﻄﻘـــﺔ اﻟﺒﺎﺣﺔ، ﻟﻤﺎ ﺗﻀﻴﻔﻪ ﻣـﻦ ﺟﻤـﺎل ﻃﺒﻴﻌـﻲ، ﻓﻀـﻼً ﻋـﻦ اﻟﻤﻮﻗـﻊ اﻟﻤﺘﻤﻴـﺰ وﺳـﻂ ﻏﺎﺑــﺔ رﻏــﺪان اﻟــﺬي ﻳﺸــﻜﻞ ﻣﻨﺘﺠﻌــﺎً ﺳــﻴﺎﺣﻴﺎً وﻣﻘﺼــﺪاً ﻟﻸﻫــﺎﻟﻲ واﻟــﺰوار ﻣــﻦ ﻣﺤﺒﻲ اﻻﺳﺘﻤﺘﺎع ﺑﺎﻟﻄﺒﻴﻌﺔ', 12, 33, 3, 'https://www.google.com/maps/search/?api=1&query=20.0201047507888,41.4321581306873', 'https://www.spa.gov.sa/_next/image?url=https%3A%2F%2Fportalcdn.spa.gov.sa%2Fbackend%2Foriginal%2F202207%2F027446228822bde73d77d14556047ae0.jpeg&w=3840&q=75', 'Lavender_Garden.html', 'طبيعية'),
(525, 'غابة رغدان', 'تشتهر غابة رغدان بأشجارها الكثيفة ومناطقها واسعة الخضرة التي تمنح الزوار والمتنزهين الاستمتاع بجمال الطبيعة، وتقع غابة رغدان في الباحة على بعد 4 كم شمال مدينة الباحة، وتعتبر من أشهر الغابات الموجودة، وتتميز بمناظرها الخلابة وجوّها المعتدل صيفًا وهطول الأمطار شتاءً، وموقعها المتميز الذي يتوسط مجموعة من القرى داخل مدينة الباحة.', 12, 34, 10, 'https://www.google.com/maps/search/?api=1&query=20.020723,41.432767', 'https://livelovesaudi.net/wp-content/uploads/2025/01/image-2-1024x712.png', 'Raghadan_Forest.html', 'طبيعية'),
(526, 'ﺣﺪﻳﻘﺔ اﻷﻣﻴﺮ ﻣﺤﻤﺪ ﺑﻦ ﺳﻌﻮد', 'ﺗﻌـﺪ أﺣـﺪ أﻓﻀـﻞ اﻟﻤﻨﺘﺰﻫــﺎت ﻓـﻲ اﻟﺒﺎﺣـﺔ ، و ﺗﺘﻤﻴـﺰ ﺑﺘﺼــﻤﻴﻢ ﻓﺮﻳـﺪ وﺑﻬـﺎ ﻣﺮاﻓــﻖ ﻣﺘﻨﻮﻋــﺔ و ﺧــﺪﻣﺎت. و ﺗﺤﺘــﻮي ﻋﻠــﻲ ﻣﺴــﺎﺣﺎت ﺧﻀــﺮاء ﺷﺎﺳــﻌﺔ ﺣﻴــﺚ ﻳﻤﻜﻨــﻚ ﻗﻀﺎء وﻗﺖ ﻣﻤﺘﻊ ﺑﺮﻓﻘﺔ اﻟﻌﺎﺋﻠﺔ.', 12, 34, 1, 'https://www.google.com/maps/search/?api=1&query=20.019124452077,41.4427018999999', 'https://bahaadmin.baha.gov.sa/Uploads/Images/62c824d4-af00-4888-a038-4e501fdbfafd_WhatsApp%20Image%202023-01-18%20at%202.31.17%20PM.jpeg', 'Prince_Mohammed_Bin_Saud_Park.html', 'طبيعية'),
(527, 'متحف الاخوين', 'ﻳﺤﺘــﻮي اﻟﻤﺘﺤــﻒ ﻋﻠــﻰ ﻗﻄــﻊ ﺗﻤﺜــﻞ ﻣﺨﺘﻠــﻒ ﺟﻮاﻧــﺐ اﻟﺤﻴــﺎة ﻓــﻲ اﻟﻤﺎﺿــﻲ ﻣــﻦ اﻷدوات اﻟﻤﻨﺰﻟﻴﺔ وأدوات اﻟﺰراﻋﺔ واﻟﺼﻨﺎﻋﺔ واﻟﺰﻳﻨﺔ واﻟﺴﻔﺮ وأﻧﻮاع اﻷﺳـﻠﺤﺔ وﻏﻴﺮﻫﺎ.', 12, 33, 2, 'https://www.google.com/maps/search/?api=1&query=20.0120746613397,41.4982965576718', 'https://lh3.googleusercontent.com/places/ANXAkqFdE0RdsDqN3FtisSUnN5douG09PKHgINnJ_ODQvCeIOu0Gw9TNAwpwXTVMKOWal5aNTsxiCNGee_9TjFGXjdwwWfWtPh9h5iY=s4800-w3852-h4800', 'Al_Akhawayn_Museum.html', 'تراثية'),
(528, 'متنزه الأمير مشاري', 'يعد متنزه الأمير مشاري بن سعود من أفضل المتنزهات في مدينة الباحة، وتقع بالقرب من غابة رغدان وتتميز بالجو اللطيف المعتدل في معظم أيام العام. وتعتبر بعض أجزاء الحديقة كمحمية طبيعية ويوجد بها جلسات وبرجولات للزوار والمتنزهين ويوجد بها مساحات خضراء شاسعة حيث يمكنك قضاء وقت ممتع مع العائلة والأصدقاء والتمشي وممارسة الرياضة، كما يوجد أيضا العديد من ملاعب الأطفال المعتادة، ومساحات للعب كرة القدم، وبعض الألعاب الكهربائية المناسبة للكبار والصغار على حد سواء.', 12, 42, 5, 'https://www.google.com/maps/search/?api=1&query=20.052205,41.397732', 'https://partner.visitsaudi.com/content/dam/map/en/pointofinterest/poi-bah-3-15/SA707297.jpg.thumb.1200.1200.jpg/', 'Prince_Mashari_Park.html', 'طبيعية'),
(529, 'منتزه الشلال', 'ﻳﻌﺪ ﻣﻨﺘﺰه اﻟﺸﻼل ﺑﻤﺤﺎﻓﻈﺔ ﺑﻨﻲ ﺣﺴﻦ، أﺣﺪ اﻟﻤﻮاﻗﻊ اﻟﺴـﻴﺎﺣﻴﺔ اﻟﺒـﺎرزة ﻓـﻲ ﻣﻨﻄﻘــﺔ اﻟﺒﺎﺣــﺔ، ﻟﻤــﺎ ﻳﻀــﻴﻔﻪ اﻟﻤﻨﺘــﺰه ﻣــﻦ ﺟﻤــﺎل ﻃﺒﻴﻌــﻲ، ﻓﻀــﻼً ﻋــﻦ اﻟﻤﻮﻗــﻊ اﻟﻤﺘﻤﻴــﺰ ﻛﻤــﺎ ﻳﻀــﻢ اﻟﺨــﺪﻣﺎت واﻟﻤﺮاﻓــﻖ اﻟﻼزﻣــﺔ ﻟﺨﺪﻣــﺔ اﻟﺰاﺋــﺮ، وﻛﻤــﺎ ﻳﺤﺘــﻮي على ﻣﻤﺮات ﻟﻠﻤﺸﺎة وأﻟﻌﺎب أﻃﻔﺎل و ﻳﺘﻤﻴﺰ ﺑﺸﻼﻻت وﺑﺤﻴﺮات ﻣﺎﺋﻴﺔ', 12, 42, 7, 'https://www.google.com/maps/search/?api=1&query=20.0609258412647,41.3900320288359', 'https://bahaadmin.baha.gov.sa/Uploads/Images/42666a9f-e109-4a51-b6da-b6ce543d5025_image16.jpeg', 'Al_Shalla_Park.html', 'طبيعية'),
(530, 'شلالات الخيرة', 'تشكل شلالات خيرة في منطقة الباحة وجهةً سياحيةً تسر الزائرين، وتقع الشلالات في غابة خيرة في قرية بني حسن في الشمال الغربي من الباحة، ويكثر فيها المدرجات الزراعية التي تحفظ الكثير من مخزون المياه بعد هطول الأمطار وتزوّد شلال خيرة أو ما يُسمى ببئر القلت بالمياه التي تستمر إلى وقت كبير من الزمن، وتنزل إلى القرية لتشكل لوحةً جمالية بمياهها الصافية ولتكون شلالاً كبيراً، وقد زوّد المتنزه بنحو 45 مظلة و15 جلسة و3 مواقع لألعاب الأطفال ودورات للمياه والعديد من الخدمات، ويجد محبو الطبيعة في غابة خيرة تنوّع فصول السنة في يوم واحد بين البرد والضباب، وتخلل ضوء الشمس بي أغصان أشجار العرعر والطلح، ما جعله ملاذًا لنيل قسط من الاستجمام والابتعاد عن صخب المدينة وضجيجها.', 12, 42, 9, 'https://www.google.com/maps/place/Wadi+Khaira+Waterfall/@20.0617236,41.3864744,17z/data=!4m10!1m2!2m1!1z2LTZhNin2YTYp9iqINin2YTYrtmK2LHYqQ!3m6!1s0x15ef49a022028c41:0xe2cec6812da42b8c!8m2!3d20.0614865!4d41.3889048!15sChnYtNmE2KfZhNin2Kog2KfZhNiu2YrYsdipWhs', 'https://vid.alarabiya.net/images/2018/02/20/23ecc3bd-1a77-4b7e-80c9-788b52bb7cfe/23ecc3bd-1a77-4b7e-80c9-788b52bb7cfe_16x9_1200x676.jpg?width=801&format=jpg', 'Al_Khayra_Waterfalls.html', 'طبيعية'),
(531, 'منتزه الشروق', 'ﻳﻤﺜﻞ ﻣﻨﺘﺰه اﻟﺸﺮوق ، أﺣﺪ اﻟﻤﻮاﻗـﻊ اﻟﺴـﻴﺎﺣﻴﺔ اﻟﺒـﺎرزة ﻓـﻲ ﻣﻨﻄﻘـﺔ اﻟﺒﺎﺣـﺔ، ﻟﻤـﺎ ﻳﻀــﻴﻔﻪ ﻣــﻦ ﺟﻤــﺎل ﻃﺒﻴﻌــﻲ، ﻓﻀــﻼً ﻋــﻦ اﻟﻤﻮﻗــﻊ اﻟﻤﺘﻤﻴــﺰ اﻟــﺬي ﻳﺸــﻜﻞ ﻣﻨﺘﺠﻌــﺎً ﺳﻴﺎﺣﻴﺎً وﻣﻘﺼﺪاً ﻟﻸﻫﺎﻟﻲ واﻟﺰوار ﻣﻦ ﻣﺤﺒﻲ اﻹﺳﺘﻤﺘﺎع ﺑﺎﻟﻄﺒﻴﻌﺔ', 12, 36, 3, 'https://www.google.com/maps/search/?api=1&query=20.0680951778421,41.5720003865077', 'https://bahaadmin.baha.gov.sa/Uploads/Images/9576da78-7263-4495-8515-cd7984d5465d_WhatsApp%20Image%202023-01-14%20at%2012.14.22%20PM%20(1).jpeg', 'Sunrise_Park.html', 'طبيعية');
